      SUBROUTINE DESATT(WSPD,VIS)                                       DES  100                                                         
C********************************************************************** DES  110                                                         
C*                                                                    * DES  120                                                         
C*    THIS SUBROUTINE CALCULATES THE ATTENUATION COEFFICIENTS AND     * DES  130                                                         
C*    ASYMMETRY PARAMETER FOR THE DESERT AEROSOL BASED ON THE WIND    * DES  140                                                         
C*    SPEED AND METEOROLOGICAL RANGE                                  * DES  150                                                         
C*                                                                    * DES  160                                                         
C*                                                                    * DES  170                                                         
C*                                                                    * DES  180                                                         
C*    PROGRAMMED BY:  D. R. LONGTIN         OPTIMETRICS, INC.         * DES  190                                                         
C*                                          BURLINGTON, MASSACHUSETTS * DES  200                                                         
C*                                          JULY 1987                 * DES  210                                                         
C*                                                                    * DES  220                                                         
C*                                                                    * DES  230                                                         
C*    INPUTS:    WSPD    -  WIND SPEED (IN M/S) AT 10 M               * DES  240                                                         
C*               VIS     -  METEOROLOGICAL RANGE (KM)                 * DES  250                                                         
C*                                                                    * DES  260                                                         
C*    OUTPUTS:   DESEXT  -  EXTINCTION COEFFICIENT AT 47 WAVELENGTHS  * DES  270                                                         
C*               DESSCA  -  SCATTERING COEFFICIENT AT 47 WAVELENGTHS  * DES  280                                                         
C*    *****      DESABS  -  ABSORPTION COEFFICIENT AT 47 WAVELENGTHS  * DES  290                                                         
C*               DESG    -  ASYMMETRY PARAMETER AT 47 WAVELENGTHS     * DES  300                                                         
C*                                                                    * DES  310                                                         
C********************************************************************** DES  320                                                         
C                                                                       DES  330                                                         
      COMMON RELHUM(34),WHNO3(34),ICH(4),VH(17),TX(63),W(63)            DES  340                                                         
      COMMON             WPATH(68, 63),TBBY(68)                         DES  350                                                         
      COMMON ABSC(5,47),EXTC(5,47),ASYM(5,47),VX2(47),AWCCON(5)         DES  360                                                         
      COMMON/ DESAER/EXT(47,4),ABS(47,4),G(47,4)                        DES  370                                                         
      DIMENSION DESEXT(47),DESSCA(47),DESABS(47),DESG(47),WIND(4)       DES  380                                                         
      REAL      DESEXT    ,DESSCA    ,DESABS    ,DESG    ,WIND          DES  390                                                         
      INTEGER WAVEL                                                     DES  400                                                         
      DATA WIND/0., 10., 20., 30./                                      DES  410                                                         
      DATA RAYSCT / 0.01159 /                                           DES  420                                                         
      IF(WSPD .LT. 0.) WSPD = 10.                                       DES  430                                                         
C                                                                       DES  440                                                         
      NWSPD = INT(WSPD/10) + 1                                          DES  450                                                         
      IF (NWSPD.GE.5) WRITE(6,999)                                      DES  460                                                         
      IF (NWSPD.GE.4) NWSPD = 3                                         DES  470                                                         
C                                                                       DES  480                                                         
C     INTERPOLATE THE RADIATIVE PROPERTIES AT WIND SPEED WSPD           DES  490                                                         
C                                                                       DES  500                                                         
      DO 100 WAVEL = 1,47                                               DES  510                                                         
C                                                                       DES  520                                                         
C     EXTINCTION COEFFICIENT                                            DES  530                                                         
C                                                                       DES  540                                                         
         SLOPE = LOG(EXT(WAVEL,NWSPD+1)/EXT(WAVEL,NWSPD))/              DES  550                                                         
     *           (WIND(NWSPD+1)-WIND(NWSPD))                            DES  560                                                         
         B = LOG(EXT(WAVEL,NWSPD+1)) - SLOPE*WIND(NWSPD+1)              DES  570                                                         
         DESEXT(WAVEL) = EXP(SLOPE*WSPD + B)                            DES  580                                                         
C                                                                       DES  590                                                         
C     ABSORPTION COEFFICIENT                                            DES  600                                                         
C                                                                       DES  610                                                         
         SLOPE = LOG(ABS(WAVEL,NWSPD+1)/ABS(WAVEL,NWSPD))/              DES  620                                                         
     *           (WIND(NWSPD+1)-WIND(NWSPD))                            DES  630                                                         
         B = LOG(ABS(WAVEL,NWSPD+1)) - SLOPE*WIND(NWSPD+1)              DES  640                                                         
         DESABS(WAVEL) = EXP(SLOPE*WSPD + B)                            DES  650                                                         
C                                                                       DES  660                                                         
C     SCATTERING COEFFICIENT                                            DES  670                                                         
C                                                                       DES  680                                                         
         DESSCA(WAVEL) = DESEXT(WAVEL) - DESABS(WAVEL)                  DES  690                                                         
C                                                                       DES  700                                                         
C     ASYMMETRY PARAMETER                                               DES  710                                                         
C                                                                       DES  720                                                         
         SLOPE = (G(WAVEL,NWSPD+1)-G(WAVEL,NWSPD))/(WIND(NWSPD+1)-      DES  730                                                         
     *           WIND(NWSPD))                                           DES  740                                                         
         B = G(WAVEL,NWSPD+1) - SLOPE*(WIND(NWSPD+1))                   DES  750                                                         
         DESG(WAVEL) = SLOPE*WSPD + B                                   DES  760                                                         
100   CONTINUE                                                          DES  770                                                         
C                                                                       DES  780                                                         
          EXT55 = DESEXT(4)                                             DES  790                                                         
C                                                                       DES  800                                                         
C         DETERMINE METEROLOGICAL RANGE FROM 0.55 EXTINCTION            DES  810                                                         
C          AND KOSCHMIEDER FORMULA                                      DES  820                                                         
C                                                                       DES  830                                                         
      IF (VIS. LE .0.) THEN                                             DES  840                                                         
               VIS = 3.912/(DESEXT(4) + RAYSCT )                        DES  850                                                         
      ENDIF                                                             DES  860                                                         
C                                                                       DES  870                                                         
C        RENORMALIZE ATTENUATION COEFFICIENTS TO 1.0 KM-1 AT            DES  880                                                         
C        0.55 MICRONS FOR CAPABILTY WITH LOWTRAN                        DES  890                                                         
C                                                                       DES  900                                                         
          DO 200 WAVEL=1,47                                             DES  910                                                         
            EXTC(1,WAVEL) = DESEXT(WAVEL)       /EXT55                  DES  920                                                         
CC          DESSCA(WAVEL) = DESSCA(WAVEL)       /EXT55                  DES  930                                                         
            ABSC(1,WAVEL) = DESABS(WAVEL)       /EXT55                  DES  940                                                         
            ASYM(1,WAVEL) = DESG(WAVEL)                                 DES  950                                                         
200      CONTINUE                                                       DES  960                                                         
       WRITE(6,900) VIS,WSPD                                            DES  970                                                         
900    FORMAT(//,'  VIS = ',F10.3,' WIND = ',F10.3)                     DES  980                                                         
       RETURN                                                           DES  990                                                         
C                                                                       DES 1000                                                         
999    FORMAT(' WARNING: WIND SPEED IS BEYOND 30 M/S; RADIATIVE',       DES 1010                                                         
     *'PROPERTIES',/,'OF THE DESERT AEROSOL HAVE BEEN EXTRAPOLATED')    DES 1020                                                         
      END                                                               DES 1030                                                         
