C***********************************************************************BAT  150                                                         
      COMMON /CONSTN/ PZERO,TZERO,AVOGAD,ALOSMT,GASCON,PLANK,BOLTZ,     BAT  160                                                         
     1    CLIGHT,ADCON,ALZERO,AVMWT,AIRMWT,AMWT(35)                     BAT  170                                                         
      DATA PZERO/1013.25/,TZERO/273.15/                                 BAT  180                                                         
      DATA AVOGAD/6.022045E+23/,ALOSMT/2.68675E+19/,                    BAT  190                                                         
     1    GASCON/8.31441E+7/,PLANK/6.626176E-27/,BOLTZ/1.380662E-16/,   BAT  200                                                         
     2    CLIGHT/2.99792458E10/                                         BAT  210                                                         
C*****ALZERO IS THE MEAN LORENTZ HALFWIDTH AT PZERO AND 296.0 K.        BAT  220                                                         
C*****AVMWT IS THE MEAN MOLECULAR WEIGHT USED TO AUTOMATICALLY          BAT  230                                                         
C*****GENERATE THE FASCODE BOUNDARIES IN AUTLAY                         BAT  240                                                         
      DATA ALZERO/0.1/,AVMWT/36.0/                                      BAT  250                                                         
      DATA AIRMWT/28.964/,AMWT/18.015,44.010,47.998,44.01,28.011,       BAT  260                                                         
     1    16.043,31.999,30.01,64.06,46.01,17.03,63.01,17.00,20.01,      BAT  270                                                         
     2    36.46,80.92,127.91,51.45,60.08,30.03,52.46,28.014,            BAT  280                                                         
     3    27.03, 50.49, 34.01, 26.03, 30.07, 34.00, 7*0./               BAT  290                                                         
      END                                                               BAT  300                                                         
      SUBROUTINE STDMDL                                                 STD  100                                                         
C***********************************************************************STD  110                                                         
C     THIS SUBROUTINE LOADS ONE OF THE 6 STANDARD ATMOSPHERIC PROFILES  STD  120                                                         
C     INTO COMMON/MODEL/ AND CALCULATES THE DENSITIES OF THE            STD  130                                                         
C     VARIOUS ABSORBING GASES AND AEROSOLS                              STD  140                                                         
C***********************************************************************STD  150                                                         
      COMMON /IFIL/IRD,IPR,IPU,NPR,IPR1                                 STD  160                                                         
      COMMON /CARD1/ MODEL,ITYPE,IEMSCT,M1,M2,M3,IM,NOPRNT,TBOUND,SALB  STD  170                                                         
      COMMON /CARD2/ IHAZE,ISEASN,IVULCN,ICSTL,ICLD,IVSA,VIS,WSS,WHH,   STD  180                                                         
     1    RAINRT                                                        STD  190                                                         
      COMMON /CARD3/ H1,H2,ANGLE,RANGE,BETA,RE,LEN                      STD  200                                                         
      COMMON /CARD4/ V1,V2,DV                                           STD  210                                                         
       COMMON /MDATA/  Z(50),P(50),T(50),WH(50),WCO2(50),WO(50),        STD  220                                                         
     X WN2O(50),WCO(50),WCH4(50),WO2(50),                               STD  230                                                         
     X CLD(50,7),RR(50,7)                                               STD  240                                                         
      COMMON /MDATA1/ WNO(50),WSO2(50),WNO2(50),WNH3(50),WAIR(50)       STD  250                                                         
      COMMON /CNSTNS/ PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                     STD  260                                                         
      COMMON /CNTRL/ KMAX,M,IKMAX,NL,ML,IKLO,ISSGEO,IMULT               STD  270                                                         
      COMMON/MODEL/   ZM(34),PM(34),TM(34),RFNDX(34),DENSTY(63,34),     STD  280                                                         
     1 CLDAMT(34),RRAMT(34),EQLWC(34),HAZEC(34)                         STD  290                                                         
      COMMON RELHUM(34),HSTOR(34),ICH(4),VH(17),TX(63),W(63)            STD  300                                                         
      COMMON             WPATH(68, 63),TBBY(68)                         STD  310                                                         
      COMMON ABSC(5,47),EXTC(5,47),ASYM(5,47),VX2(47),AWCCON(5)         STD  320                                                         
C     XLOSCH = LOSCHMIDT'S NUMBER,MOLECULES CM-2,KM-1                   STD  330                                                         
      DATA PZERO /1013.25/,TZERO/273.15/,XLOSCH/2.6868E24/              STD  340                                                         
C     RV GAS CONSTANT FOR WATER IN MB/(GM M-3 K)                        STD  350                                                         
C     CON CONVERTS WATER VAPOR FROM GM M-3 TO MOLECULES CM-2 KM-1       STD  360                                                         
      DATA RV/4.6152E-3/,CON/3.3429E21/                                 STD  370                                                         
C     CONSTANTS FOR INDEX OF REFRACTION, AFTER EDLEN, 1965              STD  380                                                         
      DATA A0/83.42/,A1/185.08/,A2/4.11/,                               STD  390                                                         
     X     B1/1.140E5/,B2/6.24E4/,C0/43.49/,C1/1.70E4/                  STD  400                                                         
C                                                                       STD  410                                                         
C     F(A) IS SATURATED WATER WAPOR DENSITY AT TEMP T,A=TZERO/T         STD  420                                                         
      F(A)=EXP(18.9766-14.9595*A-2.43882*A*A)*A                         STD  430                                                         
C                                                                       STD  440                                                         
C     CONJOE=(1/XLOSCH)*1.E5*1.E-6 WITH                                 STD  450                                                         
C        1.E5 ARISING FROM CM TO KM CONVERSION AND                      STD  460                                                         
C        1.E-6  "       "  PPMV                                         STD  470                                                         
C                                                                       STD  480                                                         
      CONJOE = 3.7194E-21                                               STD  490                                                         
C                                                                       STD  500                                                         
C     H20 CONTINUUM IS STORED AT 296 K RHZERO IS AIR DENSITY AT 296 K   STD  510                                                         
C     IN UNITS OF LOSCHMIDT'S                                           STD  520                                                         
C                                                                       STD  530                                                         
      RHZERO=(273.15/296.0)                                             STD  540                                                         
C                                                                       STD  550                                                         
C     LOAD ATMOSPHERE PROFILE INTO /MODEL/                              STD  560                                                         
      DO 25 I=1,ML                                                      STD  570                                                         
      PM(I)=P(I)                                                        STD  580                                                         
      TM(I)=T(I)                                                        STD  590                                                         
      PP=PM(I)                                                          STD  600                                                         
      TT=TM(I)                                                          STD  610                                                         
      PSS=PP/PZERO                                                      STD  620                                                         
      TSS=TZERO/TT                                                      STD  630                                                         
      F1=(PP/PZERO)/(TT/TZERO)                                          STD  640                                                         
      F2=(PP/PZERO)*SQRT(TZERO/TT)                                      STD  650                                                         
      WTEMP=WH(I)                                                       STD  660                                                         
C     SCALED H2O DENSITY                                                STD  670                                                         
      IF (ZM(I).GT.2.0) GO TO 15                                        STD  680                                                         
      TS = TZERO / TT                                                   STD  690                                                         
15    CONTINUE                                                          STD  700                                                         
C     UNIFORMALY MIXED GASES DENSITYS                                   STD  710                                                         
C     ORIGINAL TRANSMITTANCE MODEL FOR CO2+ HAS BEEN REPLACED.          STD  720                                                         
C     DENSTY(2,I)=F1*F2**0.75                                           STD  730                                                         
C     UV OZONE                                                          STD  740                                                         
C     THE UNIT FOR O3 HAS BEEN CHANGED FROM G/M**3 TO PPMV.             STD  750                                                         
      DENSTY(8,I)= CONJOE   *WAIR(I)    *WO(I)                          STD  760                                                         
C     IR OZONE                                                          STD  770                                                         
C     ORIGINAL TRANSMITTANCE MODEL HAS BEEN REPLACED.                   STD  780                                                         
C     DENSTY(3,I)= DENSTY(8,I)*F2**0.4                                  STD  790                                                         
C     N2 CONTINUUM                                                      STD  800                                                         
      DENSTY(4,I)=0.781*F1*F2                                           STD  810                                                         
C     SELF BROADENED WATER                                              STD  820                                                         
      RHOAIR = F1                                                       STD  830                                                         
      RHOH2O = CON *WTEMP/XLOSCH                                        STD  840                                                         
      RHOFRN = RHOAIR - RHOH2O                                          STD  850                                                         
      DENSTY(5,I)= XLOSCH*RHOH2O**2/RHZERO                              STD  860                                                         
C     FOREIGN BROADENED                                                 STD  870                                                         
      DENSTY(10,I)= XLOSCH*RHOH2O*RHOFRN/RHZERO                         STD  880                                                         
C     MOLECULAR SCATTERING                                              STD  890                                                         
      DENSTY(6,I) = F1                                                  STD  900                                                         
C     RELITIVE HUMIDITY WEIGHTED BY BOUNDRY LAYER AEROSOL (0 TO 2 KM)   STD  910                                                         
C                                                                       STD  920                                                         
C     LOG WEIGHTING OF REL HUMIDITY                                     STD  930                                                         
C                                                                       STD  940                                                         
      RELH = RELHUM(I)                                                  STD  950                                                         
      IF(RELHUM(I). GT.99.) RELH = 99.                                  STD  960                                                         
      RHLOG = ALOG(100. - RELH)                                         STD  970                                                         
C     DENSTY(15,I)=RELHUM(I)*DENSTY(7,I)                                STD  980                                                         
      DENSTY(15,I)=RHLOG    *DENSTY(7,I)                                STD  990                                                         
C     DENSITY (9,I) TEMP DEP OF WATER SET IN GEO                        STD 1000                                                         
      DENSTY(9,I)=0.                                                    STD 1010                                                         
C     IF(ICH(1).GT.7) DENSTY(15,I)=RELHUM(I)*DENSTY(12,I)               STD 1020                                                         
      IF(ICH(1).GT.7) DENSTY(15,I)=RHLOG    *DENSTY(12,I)               STD 1030                                                         
C     HNO3 IN ATM * CM /KM                                              STD 1040                                                         
C     NEW PROFILE IS IN UNIT OF PART PER 10**6 BY VOLUME                STD 1050                                                         
C     DENSTY(11,I)= F1* HMIX(I)*1.0E-4                                  STD 1060                                                         
C     IF(MODEL.EQ.7) DENSTY(11,I)=F1*HSTOR(I)*1.0E-4                    STD 1070                                                         
                     DENSTY(11,I)=F1*HSTOR(I)*1.0E-4                    STD 1080                                                         
C                                                                       STD 1090                                                         
C      O2 TEMP DEP                                                      STD 1100                                                         
C                                                                       STD 1110                                                         
      DT = TT  - 220.                                                   STD 1120                                                         
      WO2D       = CONJOE   *WAIR(I)    *WO2(I)  * PSS                  STD 1130                                                         
C                                                                       STD 1140                                                         
C     DT CAN BE NEGIVATIVE                                              STD 1150                                                         
C     EFFECTIVE DT CALCULATED IN TRANS                                  STD 1160                                                         
C                                                                       STD 1170                                                         
      DENSTY(1,I)  = WO2D * TT                                          STD 1180                                                         
      DENSTY(2,I)  = WO2D * DT * DT                                     STD 1190                                                         
      DENSTY(63,I) = WO2D                                               STD 1200                                                         
C                                                                       STD 1210                                                         
C   NEW  MICROWAVE TEMP  RAIN                                           STD 1220                                                         
C                                                                       STD 1230                                                         
      DENSTY(3,I) = 0.                                                  STD 1240                                                         
      DENSTY(61,I)= 0.                                                  STD 1250                                                         
      DENSTY(62,I)= 0.                                                  STD 1260                                                         
      IF(RRAMT(I) .NE. 0.) THEN                                         STD 1270                                                         
           DENSTY(3,I) = RRAMT(I)**(.63)                                STD 1280                                                         
           DENSTY(61,I)= DENSTY(3,I) * T(I)                             STD 1290                                                         
           DENSTY(62,I)= 1.                                             STD 1300                                                         
      ENDIF                                                             STD 1310                                                         
C                                                                       STD 1320                                                         
C       CIRRIUS CLOUD                                                   STD 1330                                                         
       IF(ICLD.LT.18) DENSTY(16,I) = 0.                                 STD 1340                                                         
C  --- FOR H2O -----                                                    STD 1350                                                         
      CONH2O=WH(I)  *.1                                                 STD 1360                                                         
      DENSTY(17,I)=CONH2O*PSS**0.9810*TSS**( 0.3324)                    STD 1370                                                         
      DENSTY(18,I)=CONH2O*PSS**1.1406*TSS**(-2.6343)                    STD 1380                                                         
      DENSTY(19,I)=CONH2O*PSS**0.9834*TSS**(-2.5294)                    STD 1390                                                         
      DENSTY(20,I)=CONH2O*PSS**1.0443*TSS**(-2.4359)                    STD 1400                                                         
      DENSTY(21,I)=CONH2O*PSS**0.9681*TSS**(-1.9537)                    STD*1410                                                         
      DENSTY(22,I)=CONH2O*PSS**0.9555*TSS**(-1.5378)                    STD 1420                                                         
      DENSTY(23,I)=CONH2O*PSS**0.9362*TSS**(-1.6338)                    STD 1430                                                         
      DENSTY(24,I)=CONH2O*PSS**0.9233*TSS**(-0.9398)                    STD 1440                                                         
      DENSTY(25,I)=CONH2O*PSS**0.8658*TSS**(-0.1034)                    STD 1450                                                         
      DENSTY(26,I)=CONH2O*PSS**0.8874*TSS**(-0.2576)                    STD 1460                                                         
      DENSTY(27,I)=CONH2O*PSS**0.7982*TSS**( 0.0588)                    STD 1470                                                         
      DENSTY(28,I)=CONH2O*PSS**0.8088*TSS**( 0.2816)                    STD 1480                                                         
      DENSTY(29,I)=CONH2O*PSS**0.6642*TSS**( 0.2764)                    STD 1490                                                         
      DENSTY(30,I)=CONH2O*PSS**0.6656*TSS**( 0.5061)                    STD 1500                                                         
C  --- FOR O3                                                           STD 1510                                                         
      CONO3 = CONJOE   *WAIR(I)    *WO(I)                               STD 1520                                                         
      DENSTY(31,I)=CONO3 *PSS**0.4200*TSS**( 1.3909)                    STD 1530                                                         
      DENSTY(32,I)=CONO3 *PSS**0.4221*TSS**( 0.7678)                    STD 1540                                                         
      DENSTY(33,I)=CONO3 *PSS**0.3739*TSS**( 0.1225)                    STD 1550                                                         
      DENSTY(34,I)=CONO3 *PSS**0.1770*TSS**( 0.9827)                    STD 1560                                                         
      DENSTY(35,I)=CONO3 *PSS**0.3921*TSS**( 0.1942)                    STD 1570                                                         
C  --- FOR CO2                                                          STD 1580                                                         
      CONCO2= CONJOE   *WAIR(I)  *WCO2(I)                               STD 1590                                                         
      DENSTY(36,I)=CONCO2*PSS**0.6705*TSS**(-2.2560)                    STD 1600                                                         
      DENSTY(37,I)=CONCO2*PSS**0.7038*TSS**(-5.0768)                    STD 1610                                                         
      DENSTY(38,I)=CONCO2*PSS**0.7258*TSS**(-1.6740)                    STD 1620                                                         
      DENSTY(39,I)=CONCO2*PSS**0.6982*TSS**(-1.8107)                    STD 1630                                                         
      DENSTY(40,I)=CONCO2*PSS**0.8867*TSS**(-0.5327)                    STD 1640                                                         
      DENSTY(41,I)=CONCO2*PSS**0.7883*TSS**(-1.3244)                    STD 1650                                                         
      DENSTY(42,I)=CONCO2*PSS**0.6899*TSS**(-0.8152)                    STD 1660                                                         
      DENSTY(43,I)=CONCO2*PSS**0.6035*TSS**( 0.6026)                    STD 1670                                                         
C  --- FOR CO                                                           STD 1680                                                         
      CONCO = CONJOE   *WAIR(I)  *WCO (I)                               STD 1690                                                         
      DENSTY(44,I)=CONCO *PSS**0.7589*TSS**( 0.6911)                    STD 1700                                                         
      DENSTY(45,I)=CONCO *PSS**0.9267*TSS**( 0.1716)                    STD 1710                                                         
C  --- FOR CH4                                                          STD 1720                                                         
      CONCH4= CONJOE   *WAIR(I)  *WCH4(I)                               STD 1730                                                         
      DENSTY(46,I)=CONCH4*PSS**0.7139*TSS**(-0.4185)                    STD 1740                                                         
C  --- FOR N2O                                                          STD 1750                                                         
      CONN2O= CONJOE   *WAIR(I)  *WN2O(I)                               STD 1760                                                         
      DENSTY(47,I)=CONN2O*PSS**0.3783*TSS**( 0.9399)                    STD 1770                                                         
      DENSTY(48,I)=CONN2O*PSS**0.7203*TSS**(-0.1836)                    STD 1780                                                         
      DENSTY(49,I)=CONN2O*PSS**0.7764*TSS**( 1.1931)                    STD 1790                                                         
C  --- FOR O2                                                           STD 1800                                                         
      CONO2 = CONJOE   *WAIR(I)  *WO2 (I)                               STD 1810                                                         
      DENSTY(50,I)=CONO2 *PSS**1.1879*TSS**( 2.9738)                    STD 1820                                                         
      DENSTY(51,I)=CONO2 *PSS**0.9353*TSS**( 0.1936)                    STD 1830                                                         
C  --- FOR NH3                                                          STD 1840                                                         
      CONNH3= CONJOE   *WAIR(I)  *WNH3(I)                               STD 1850                                                         
      DENSTY(52,I)=CONNH3*PSS**0.8023*TSS**(-0.9111)                    STD 1860                                                         
      DENSTY(53,I)=CONNH3*PSS**0.6968*TSS**( 0.3377)                    STD 1870                                                         
C  --- FOR NO                                                           STD 1880                                                         
      CONNO = CONJOE   *WAIR(I)  *WNO (I)                               STD 1890                                                         
      DENSTY(54,I)=CONNO *PSS**0.5265*TSS**(-0.4702)                    STD 1900                                                         
C  --- FOR NO2                                                          STD 1910                                                         
      CONNO2= CONJOE   *WAIR(I)  *WNO2(I)                               STD 1920                                                         
      DENSTY(55,I)=CONNO2*PSS**0.3956*TSS**(-0.0545)                    STD 1930                                                         
C  --- FOR SO2                                                          STD 1940                                                         
      CONSO2= CONJOE   *WAIR(I)  *WSO2(I)                               STD 1950                                                         
      DENSTY(56,I)=CONSO2*PSS**0.2943*TSS**( 1.2316)                    STD 1960                                                         
      DENSTY(57,I)=CONSO2*PSS**0.2135*TSS**( 0.0733)                    STD 1970                                                         
C***********************************************************************STD 1980                                                         
C   HERZBERG CONTINUUM PRESSURE DEPENDENCE CALCULATION, SHARDANAND 1977 STD 1990                                                         
C      AND   YOSHINO ET AL 1988                                         STD 2000                                                         
C                                                                       STD 2010                                                         
C     OXYGEN                                                            STD 2020                                                         
C                                                                       STD 2030                                                         
      DENSTY(58,I)=(1.+.83*F1)*CONO2                                    STD*2042                                                         
      DENSTY(59,I) = 0.                                                 STD 2050                                                         
      DENSTY(60,I) = 0.                                                 STD 2060                                                         
C***********************************************************************STD 2070                                                         
C                                                                       STD 2080                                                         
C     RFNDX = REFRAXTIVITY 1-INDEX OF REFRACTION                        STD 2090                                                         
C     FROM EDLEN, 1966                                                  STD 2100                                                         
      PPW=RV*WTEMP*TT                                                   STD 2110                                                         
      AVW=0.5*(V1+V2)                                                   STD 2120                                                         
      RFNDX(I)=((A0+A1/(1.-(AVW/B1)**2) +A2/(1.0-(AVW/B2)**2))*         STD 2130                                                         
     X (PP/PZERO)*(TZERO+15.0)/TT-(C0-(AVW/C1)**2)*PPW/PZERO)*1.E-6     STD 2140                                                         
25    CONTINUE                                                          STD 2150                                                         
      IF(NPR.EQ.1) GO TO 40                                             STD 2160                                                         
      WRITE(IPR,910)                                                    STD 2170                                                         
      DO 30 I=1,ML                                                      STD 2180                                                         
       WRITE(IPR,905) I,ZM(I),PM(I),TM(I),(DENSTY(K,I),K=4,6),RFNDX(I), STD 2190                                                         
     X DENSTY(8,I),DENSTY(58,I)                                         STD 2200                                                         
30    CONTINUE                                                          STD 2210                                                         
      WRITE (IPR,915)                                                   STD 2220                                                         
      DO 35 I=1,ML                                                      STD 2230                                                         
      WRITE(IPR,905) I,ZM(I),PM(I),TM(I),(DENSTY(K,I),K=10,11),         STD 2240                                                         
     X DENSTY(7,I),DENSTY(12,I),DENSTY(13,I),DENSTY(14,I),DENSTY(15,I), STD 2250                                                         
     X DENSTY(16,I),RELHUM(I)                                           STD 2260                                                         
35    CONTINUE                                                          STD 2270                                                         
      WRITE(IPR,920)                                                    STD 2280                                                         
      DO 39 I = 1,ML                                                    STD 2290                                                         
      WRITE(IPR,906)I,ZM(I),PM(I),TM(I),DENSTY(17,I),DENSTY(31,I),      STD 2300                                                         
     + DENSTY(36,I),DENSTY(44,I),DENSTY(46,I),DENSTY(47,I),             STD 2310                                                         
     + DENSTY(50,I),DENSTY(52,I),DENSTY(54,I),DENSTY(55,I),DENSTY(56,I) STD 2320                                                         
   39 CONTINUE                                                          STD 2330                                                         
40    CONTINUE                                                          STD 2340                                                         
      RETURN                                                            STD 2350                                                         
  905 FORMAT (I4,0PF9.2,F9.3,F7.1,1X,1P9E10.3)                          STD 2360                                                         
  906 FORMAT (I4,0PF9.2,F9.3,F7.1,1X,1P11E9.2)                          STD 2370                                                         
910   FORMAT('1',/,'  ATMOSPHERIC PROFILES',//,                         STD 2380                                                         
     1 3X,'I',T10,'Z',T18,'P',T26,'T',                                  STD 2390                                                         
     2 T35,'N2',T44,'CNTMSLF',T52,'MOL SCAT',T65,'N-1', T73,            STD 2400                                                         
     3 'O3 (UV)',T82,'O2 (UV)',/,                                       STD 2410                                                         
     4 T9,'(KM)',T17,'(MB)',T25,'(K)',                                  STD 2420                                                         
     5 T40,'(MOL/CM2 KM)',T55,'(-)',T65,'(-)',T 70,                     STD 2430                                                         
     6 '(ATM CM/KM)',T82,'(ATM CM/KM)',/)                               STD 2440                                                         
915   FORMAT('1',/,'  ATMOSPHERIC PROFILES',//,                         STD 2450                                                         
     1 3X,'I',T10,'Z',T18,'P',T26,'T',T33,'CNTMFRN',T45,'HNO3',         STD 2460                                                         
     2 T53,'AEROSOL 1',T63,'AEROSOL 2', T73,'AEROSOL 3',T83,            STD 2470                                                         
     3 'AEROSOL 4',T93,'AER1*RH',T103,'CIRRUS',T118,'RH'/,              STD 2480                                                         
     4 T9,'(KM)',T17,'(MB)',T25,'(K)',T31,'MOL/CM2 KM',T42,             STD 2490                                                         
     5 'ATM CM/KM',T54,'(-)',T64,'(-)',T74,'(-)',T84,'(-)',T94,         STD 2500                                                         
     6 '(-)',T104,'(-)',T113,'(PERCNT)',/)                              STD 2510                                                         
  920 FORMAT('1',/,'  ATMOSPHERIC PROFILES',//,                         STD 2520                                                         
     + '  (IF A MOLECULE HAS MORE THAN ONE BAND, THEN THE DATA FOR THE',STD 2530                                                         
     + ' FIRST BAND ARE SHOWN.)'//                                      STD 2540                                                         
     1 '   I      Z       P       T      H2O      O3       CO2',        STD 2550                                                         
     2 '      CO       CH4      N2O      O2       NH3      NO ',        STD 2560                                                         
     3 '      NO2      SO2'/                                            STD 2570                                                         
     4 '         (KM)    (MB)    (K) G/CM**2/KM  (            ',        STD 2580                                                         
     5 '          ATM CM/KM                                   ',        STD 2590                                                         
     6 '                  )')                                           STD 2600                                                         
      END                                                               STD 2610                                                         
      SUBROUTINE LAYCLD(K,CLDATZ,RRATZ,ICLD1,GNDALT,RAINRT)             LAY  100                                                         
C                                                                       LAY  110                                                         
C      THIS SUBROUTINE RESTRUCTURES THE ATMOSPHERIC PROFILE             LAY  120                                                         
C      TO PROFIDE FINER LAYERING WITHIN THE FIRST 6 KM.                 LAY  130                                                         
C                                                                       LAY  140                                                         
C     ZMDL COMMON /MODEL/ FINAL ALTITUDE FOR LOWTRAN                    LAY  150                                                         
C     ZK  EFFECTIVE CLOUD ALTITUDES                                     LAY  160                                                         
C     ZCLD CLOUD ALTITUDE ARRAY                                         LAY  170                                                         
C     ZDIF  ALT DIFF OF 2 LAYERS                                        LAY  180                                                         
C     ZDA COMMON /MDATA/ CLD AND RAIN INFO IN THIS COMMON               LAY  190                                                         
C                                                                       LAY  200                                                         
       COMMON /MDATA/  Z(50),P(50),T(50),WH(50),WCO2(50),WO(50),        LAY  210                                                         
     X WN2O(50),WCO(50),WCH4(50),WO2(50),                               LAY  220                                                         
     X CLD(50,7),RR(50,7)                                               LAY  230                                                         
      COMMON /MDATA1/ WNO(50),WSO2(50),WNO2(50),WNH3(50),WAIR(50)       LAY  240                                                         
      COMMON/MODEL/ ZMDL(34),PM(34),TM(34),RFNDX(34),DENSTY(63,34),     LAY  250                                                         
     1 CLDAMT(34),RRAMT(34),EQLWC(34),HAZEC(34)                         LAY  260                                                         
      DIMENSION ZCLD(16)                                                LAY  270                                                         
      DATA ZCLD/ 0.0,0.16,0.33,0.66,1.0,1.5,2.0,2.4,2.7,                LAY  280                                                         
     1 3.0,3.5,4.0,4.5,5.0,5.5,6.0/                                     LAY  290                                                         
      DATA CLDTP/6.0001/                                                LAY  300                                                         
      DATA DELZ /0.02/                                                  LAY  310                                                         
      ICLD=ICLD1                                                        LAY  320                                                         
      IF(ICLD .EQ. 0) RETURN                                            LAY  330                                                         
      IF(ICLD .GT. 11)RETURN                                            LAY  340                                                         
      ZK=ZMDL(K)-GNDALT                                                 LAY  350                                                         
      IF(ZK.LT.0.) ZK=0.                                                LAY  360                                                         
      IF(ZMDL(K).GT.6.) ZK=ZMDL(K)                                      LAY  370                                                         
      IF(ICLD.GT.5 )  GO TO 15                                          LAY  380                                                         
CCC                                                                     LAY  390                                                         
CCC    ICLD  IS  1- 5 ONE OF 5 SPECIFIC CLOUD MODELS IS CHOSEN          LAY  400                                                         
CCC                                                                     LAY  410                                                         
      MC=ICLD                                                           LAY  420                                                         
      MR=6                                                              LAY  430                                                         
      GO TO 25                                                          LAY  440                                                         
15    CONTINUE                                                          LAY  450                                                         
CCC                                                                     LAY  460                                                         
CCC   ICLD  IS  6-10 ONE OF 5 SPECIFIC CLOUD/RAIN MODELS CHOSEN         LAY  470                                                         
CCC                                                                     LAY  480                                                         
      IF(ICLD .EQ. 6) MC=3                                              LAY  490                                                         
      IF(ICLD .EQ. 7 .OR. ICLD .EQ. 8) MC=5                             LAY  500                                                         
      IF(ICLD .GT. 8) MC=1                                              LAY  510                                                         
      MR=ICLD-5                                                         LAY  520                                                         
25    IF(  ZK.GT.CLDTP) GO TO 30                                        LAY  530                                                         
      CLDATZ=0.                                                         LAY  540                                                         
      RRATZ=0.                                                          LAY  550                                                         
      IF(ZK.LE.10.)RRATZ=RAINRT                                         LAY  560                                                         
      IF(MC.LT.1) GO TO 29                                              LAY  570                                                         
      DO 26 MK=1,15                                                     LAY  580                                                         
      IF(  ZK.GE.ZCLD(MK+1)) GO TO 26                                   LAY  590                                                         
      IF(  ZK.LT.ZCLD(MK)) GO TO 26                                     LAY  600                                                         
      IF(ABS(  ZK-ZCLD(MK)).LT.DELZ) GO TO 27                           LAY  610                                                         
      GO TO 28                                                          LAY  620                                                         
27     CLDATZ=CLD(MK,MC)                                                LAY  630                                                         
       RRATZ=RR(MK,MR)                                                  LAY  640                                                         
      GO TO 29                                                          LAY  650                                                         
28    ZDIF=ZCLD(MK+1)-ZCLD(MK)                                          LAY  660                                                         
      IF(ZDIF.LT.DELZ) GO TO 27                                         LAY  670                                                         
      FAC=(ZCLD(MK+1)-  ZK)/ZDIF                                        LAY  680                                                         
      CLDATZ=CLD(MK+1,MC)+FAC*(CLD(MK,MC)-CLD(MK+1,MC))                 LAY  690                                                         
      RRATZ=RR(MK+1,MR)+FAC*(RR(MK,MR)-RR(MK+1,MR))                     LAY  700                                                         
      GO TO 29                                                          LAY  710                                                         
26    CONTINUE                                                          LAY  720                                                         
29    CLDAMT(K)=CLDATZ                                                  LAY  730                                                         
      CLD(K,7)=CLDATZ                                                   LAY  740                                                         
      RR(K,7)=RRATZ                                                     LAY  750                                                         
      RRAMT(K)=RRATZ                                                    LAY  760                                                         
      RETURN                                                            LAY  770                                                         
30    CONTINUE                                                          LAY  780                                                         
      CLDAMT(K)=0.0                                                     LAY  790                                                         
      RRAMT(K)=0.0                                                      LAY  800                                                         
      CLDATZ=0.0                                                        LAY  810                                                         
      RRATZ=0.0                                                         LAY  820                                                         
      RETURN                                                            LAY  830                                                         
C100  CONTINUE                                                          LAY  840                                                         
C     RETURN                                                            LAY  850                                                         
      END                                                               LAY  860                                                         
