      SUBROUTINE CHECK(V,IV,KEY)                                        CHE  100                                                         
C                                                                       CHE  110                                                         
C      UNITS CONVERSION FOR P AND T                                     CHE  120                                                         
C                                                                       CHE  130                                                         
C     V = P OR T     AND  IV =JUNITP(I.E. MB,ATM,TORR)                  CHE  140                                                         
C                            =JUNITT(I.E. DEG K OR C)                   CHE  150                                                         
C                            =JUNITR(I.E. KM,M,OR CM)                   CHE  160                                                         
C                                                                       CHE  170                                                         
      DATA PMB/1013.25/,PTORR/760./,DEGK/273.15/                        CHE  180                                                         
      IF(IV.LE.10) RETURN                                               CHE  190                                                         
      GO TO(100,200,300) KEY                                            CHE  200                                                         
C                                                                       CHE  210                                                         
C      PRESSURE CONVERSIONS                                             CHE  220                                                         
C                                                                       CHE  230                                                         
  100 IF(IV.EQ.11)GO TO 110                                             CHE  240                                                         
      IF(IV.EQ.12)GO TO 120                                             CHE  250                                                         
      STOP'CHECK(P)'                                                    CHE  260                                                         
  110 V=V*PMB                                                           CHE  270                                                         
      RETURN                                                            CHE  280                                                         
  120 V=V*PMB/PTORR                                                     CHE  290                                                         
      RETURN                                                            CHE  300                                                         
C                                                                       CHE  310                                                         
C      TEMPERATURE COMVERSIONS                                          CHE  320                                                         
C                                                                       CHE  330                                                         
  200 IF(IV.GT.11)STOP'CHECK(T)'                                        CHE  340                                                         
      V=V+DEGK                                                          CHE  350                                                         
      RETURN                                                            CHE  360                                                         
C                                                                       CHE  370                                                         
C      RANGE CONVERSIONS                                                CHE  380                                                         
C                                                                       CHE  390                                                         
  300 IF(IV.EQ.11)GO TO 310                                             CHE  400                                                         
      IF(IV.EQ.12)GO TO 320                                             CHE  410                                                         
      STOP'CHECK(R)'                                                    CHE  420                                                         
  310 V=V/1.E3                                                          CHE  430                                                         
      RETURN                                                            CHE  440                                                         
  320 V=V/1.E5                                                          CHE  450                                                         
      RETURN                                                            CHE  460                                                         
      END                                                               CHE  470                                                         
      SUBROUTINE VSANSM(K,AHAZE,IHA1)                                   VSA  100                                                         
      COMMON RELHUM(34),WHNO3(34),ICH(4),VH(17),TX(63),W(63)            VSA  110                                                         
      COMMON             WPATH(68, 63),TBBY(68)                         VSA  120                                                         
      COMMON ABSC(5,47),EXTC(5,47),ASYM(5,47),VX2(47),AWCCON(5)         VSA  130                                                         
      COMMON /IFIL/IRD,IPR,IPU,NPR,IPR1                                 VSA  140                                                         
      COMMON /CNTRL/ KMAX,M,IKMAX,NL,ML,IKLO,ISSGEO,IMULT               VSA  150                                                         
      COMMON /CARD1/ MODEL,ITYPE,IEMSCT,M1,M2,M3,IM,NOPRNT,TBOUND,SALB  VSA  160                                                         
      COMMON /CARD1B/ JUNIT(15),WMOLI(12),WAIR1,JLOW                    VSA  170                                                         
      COMMON /CARD2/ IHAZE,ISEASN,IVULCN,ICSTL,ICIR,IVSA,VIS,WSS,WHH,   VSA  180                                                         
     1    RAINRT                                                        VSA  190                                                         
      COMMON /ZVSALY/ ZVSA(10),RHVSA(10),AHVSA(10),IHVSA(10)            VSA  200                                                         
      COMMON /NSINP/ ZMDL(40),PM(40),TM(40),WMDL(40,13)                 VSA  210                                                         
C                                                                       VSA  220                                                         
      COMMON /MDATA/  Z(50),P(50),T(50),WH(50),WCO2(50),WO(50),         VSA  230                                                         
     X WN2O(50),WCO(50),WCH4(50),WO2(50),                               VSA  240                                                         
     X CLD(50,7),RR(50,7)                                               VSA  250                                                         
      COMMON /MDATA1/ WNO(50),WSO2(50),WNO2(50),WNH3(50),WAIR(50)       VSA  260                                                         
      DIMENSION WMOL(13)                                                VSA  270                                                         
C                                                                       VSA  280                                                         
C     OUTPUT COMMON MDATA AND MDATA1                                    VSA  290                                                         
C                                                                       VSA  300                                                         
C                                                                       VSA  310                                                         
C     MODEL 7 CODEING                                                   VSA  320                                                         
C     OLD LAYERS  AEROSOL RETURNED                                      VSA  330                                                         
C     NEW LAYERS P,T,DP,AEROSOL                                         VSA  340                                                         
C                                                                       VSA  350                                                         
C                                                                       VSA  360                                                         
C                                                                       VSA  370                                                         
      JML=ML                                                            VSA  380                                                         
      J=1                                                               VSA  390                                                         
      KN=K                                                              VSA  400                                                         
110   IF(KN.GT.10)GO TO 140                                             VSA  410                                                         
      JL=J-1                                                            VSA  420                                                         
      IF(JL.LT.1)JL=1                                                   VSA  430                                                         
      JP=JL+1                                                           VSA  440                                                         
      JLS = JL                                                          VSA  450                                                         
      IF(ZVSA(KN).EQ.ZMDL  (JL))GO TO 140                               VSA  460                                                         
      JLS = JP                                                          VSA  470                                                         
      IF(ZVSA(KN).EQ.ZMDL  (JP))GO TO 140                               VSA  480                                                         
      IF(ZVSA(KN).GT.ZMDL  (JL).AND.ZVSA(KN).LT.ZMDL  (JP))GO TO 115    VSA  490                                                         
      IF(J. GE. JML) GO TO 115                                          VSA  500                                                         
      J = J + 1                                                         VSA  510                                                         
      GO TO 110                                                         VSA  520                                                         
115   Z(K)=ZVSA(KN)                                                     VSA  530                                                         
      DIF=ZMDL  (JP)-ZMDL  (JL)                                         VSA  540                                                         
      DZ=ZVSA(KN)-ZMDL  (JL)                                            VSA  550                                                         
      DLIN=DZ/DIF                                                       VSA  560                                                         
      P(K)  = (PM(JP)-PM(JL))*DLIN+PM(JL)                               VSA  570                                                         
      T(K)   =(TM(JP)-TM(JL))*DLIN+TM(JL)                               VSA  580                                                         
      DO 120 KM = 1,13                                                  VSA  590                                                         
      WMOL(KM)=(WMDL(JP,KM)-WMDL(JL,KM))*DLIN+WMDL(JL,KM)               VSA  600                                                         
120   CONTINUE                                                          VSA  610                                                         
      IHA1  =IHVSA(KN)                                                  VSA  620                                                         
      AHAZE  =AHVSA(KN)                                                 VSA  630                                                         
      FAC=(ZVSA(KN)-ZMDL  (JL))/DIF                                     VSA  640                                                         
      IF(PM(JP).GT.0.0.AND.PM(JL).GT.0.) THEN                           VSA  650                                                         
           P(K)  =PM(JL)*(PM(JP)/PM(JL))**FAC                           VSA  660                                                         
      ENDIF                                                             VSA  670                                                         
      IF(TM(JP).GT.0.0.AND.TM(JL).GT.0.) THEN                           VSA  680                                                         
           T(K)   =TM(JL)*(TM(JP)/TM(JL))**FAC                          VSA  690                                                         
      ENDIF                                                             VSA  700                                                         
      DO 130 KM = 1,13                                                  VSA  710                                                         
      IF(WMDL(JP,KM) .GT.0.0.AND.WMDL(JL,KM).GT.0.0) THEN               VSA  720                                                         
           WMOL(KM)=(WMDL(JL,KM)*(WMDL(JP,KM))/WMDL(JL,KM))**FAC        VSA  730                                                         
      ENDIF                                                             VSA  740                                                         
130   CONTINUE                                                          VSA  750                                                         
       WH(K)    = WMOL(1)                                               VSA  760                                                         
       WCO2(K)  = WMOL(2)                                               VSA  770                                                         
       WO(K)    = WMOL(3)                                               VSA  780                                                         
       WN2O(K)  = WMOL(4)                                               VSA  790                                                         
       WCO(K)   = WMOL(5)                                               VSA  800                                                         
       WCH4(K)  = WMOL(6)                                               VSA  810                                                         
       WO2(K)   = WMOL(7)                                               VSA  820                                                         
       WNO(K)   = WMOL(8)                                               VSA  830                                                         
       WSO2(K)  = WMOL(9)                                               VSA  840                                                         
       WNO2(K)  = WMOL(10)                                              VSA  850                                                         
       WNH3(K)  = WMOL(11)                                              VSA  860                                                         
       WHNO3(K) = WMOL(12)                                              VSA  870                                                         
       WAIR(K)  = WMOL(13)                                              VSA  880                                                         
      RETURN                                                            VSA  890                                                         
140   CONTINUE                                                          VSA  900                                                         
      J = JLS                                                           VSA  910                                                         
      IF(K.GT.10) THEN                                                  VSA  920                                                         
         J = K - 10 + JLOW                                              VSA  930                                                         
         IHA1  =0                                                       VSA  940                                                         
         AHAZE  =0.                                                     VSA  950                                                         
      ENDIF                                                             VSA  960                                                         
      Z(K) = ZMDL(J)                                                    VSA  970                                                         
      P(K)  =PM(J)                                                      VSA  980                                                         
      T(K) = TM(J)                                                      VSA  990                                                         
      DO 135 KM = 1,13                                                  VSA 1000                                                         
      WMOL(KM)= WMDL(J,KM)                                              VSA 1010                                                         
135   CONTINUE                                                          VSA 1020                                                         
       WH(K)    = WMOL(1)                                               VSA 1030                                                         
       WCO2(K)  = WMOL(2)                                               VSA 1040                                                         
       WO(K)    = WMOL(3)                                               VSA 1050                                                         
       WN2O(K)  = WMOL(4)                                               VSA 1060                                                         
       WCO(K)   = WMOL(5)                                               VSA 1070                                                         
       WCH4(K)  = WMOL(6)                                               VSA 1080                                                         
       WO2(K)   = WMOL(7)                                               VSA 1090                                                         
       WNO(K)   = WMOL(8)                                               VSA 1100                                                         
       WSO2(K)  = WMOL(9)                                               VSA 1110                                                         
       WNO2(K)  = WMOL(10)                                              VSA 1120                                                         
       WNH3(K)  = WMOL(11)                                              VSA 1130                                                         
       WHNO3(K) = WMOL(12)                                              VSA 1140                                                         
       WAIR(K)  = WMOL(13)                                              VSA 1150                                                         
      IF(KN.LE.9) IHA1  =IHVSA(KN)                                      VSA 1160                                                         
      IF(KN.LE.9)AHAZE  =AHVSA(KN)                                      VSA 1170                                                         
      RETURN                                                            VSA 1180                                                         
      END                                                               VSA 1190                                                         
