C                                                                       RDN  960                                                         
      READ(IRD,80)ZMDL(K),P(K),T(K),WMOL(1),WMOL(2),WMOL(3),            RDN  970                                                         
     X (JCHAR(KM),KM=1,15)                                              RDN  980                                                         
80    FORMAT ( F10.3,5E10.3,15A1)                                       RDN  990                                                         
       WRITE(IPR,81)ZMDL(K),P(K),T(K),WMOL(1),WMOL(2),WMOL(3),          RDN 1000                                                         
     X (JCHAR(KM),KM=1,15)                                              RDN 1010                                                         
81    FORMAT ( F10.3,1P5E10.3,10X,15A1)                                 RDN 1020                                                         
      IF(ZMDL(K) .LE. 2.0)JLOW = K                                      RDN 1030                                                         
      IF(IRD1 .EQ. 1) THEN                                              RDN 1040                                                         
           READ(IRD,83)(WMOL(KM),KM=4,12)                               RDN 1050                                                         
83         FORMAT(8E10.3)                                               RDN 1060                                                         
           WRITE(IPR,84)(WMOL(KM),KM=4,12)                              RDN 1070                                                         
84         FORMAT(1P8E10.3)                                             RDN 1080                                                         
      ENDIF                                                             RDN 1090                                                         
C                                                                       RDN 1100                                                         
C                                                                       RDN 1110                                                         
C     AHAZE =  AEROSOL VISIBLE EXTINCTION COFF (KM-1)                   RDN 1120                                                         
C     AT A WAVELENGTH OF 0.55 MICROMETERS                               RDN 1130                                                         
C                                                                       RDN 1140                                                         
C     EQLWCZ=LIQUID WATER CONTENT (PPMV) AT ALT Z                       RDN 1150                                                         
C            FOR AEROSOL, CLOUD OR FOG MODELS                           RDN 1160                                                         
C                                                                       RDN 1170                                                         
C     RRATZ=RAIN RATE (MM/HR) AT ALT Z                                  RDN 1180                                                         
C                                                                       RDN 1190                                                         
C     IHA1 AEROSOL MODEL USED FOR SPECTRAL DEPENDENCE OF EXTINCTION     RDN 1200                                                         
C                                                                       RDN 1210                                                         
C     IVUL1 STRATOSPHERIC AERSOL MODEL USED FOR SPECTRAL DEPENDENCE     RDN 1220                                                         
C     OF EXT AT Z                                                       RDN 1230                                                         
C                                                                       RDN 1240                                                         
C     ICLD1 CLOUD MODEL USED FOR SPECTRAL DEPENDENCE OF EXT AT Z        RDN 1250                                                         
C                                                                       RDN 1260                                                         
C     ONLY ONE OF IHA1,ICLD1  OR IVUL1 IS ALLOWED                       RDN 1270                                                         
C     IHA1 NE 0 OTHERS IGNORED                                          RDN 1280                                                         
C     IHA1 EQ 0 AND ICLD1 NE 0 USE ICLD1                                RDN 1290                                                         
C                                                                       RDN 1300                                                         
C     IF AHAZE AND EQLWCZ ARE BOUTH ZERO                                RDN 1310                                                         
C        DEFAULT PROFILE ARE LOADED FROM IHA1,ICLD1,IVUL1               RDN 1320                                                         
C     ISEA1 = AERSOL SEASON CONTROL FOR ALTITUDE Z                      RDN 1330                                                         
C                                                                       RDN 1340                                                         
      IF(IRD2 .EQ. 1) THEN                                              RDN 1350                                                         
           READ(IRD,82)    AHAZE,EQLWCZ,RRATZ,IHA1,ICLD1,IVUL1,ISEA1    RDN 1360                                                         
           WRITE(IPR,82)    AHAZE,EQLWCZ,RRATZ,IHA1,ICLD1,IVUL1,ISEA1   RDN 1370                                                         
82         FORMAT(10X,3F10.3,4I5)                                       RDN 1380                                                         
      ENDIF                                                             RDN 1390                                                         
      DO 12 KM = 1,15                                                   RDN 1400                                                         
12    JUNIT(KM) = JOU(JCHAR(KM))                                        RDN 1410                                                         
      IF(M1 .NE. 0) JUNIT(1) = M1                                       RDN 1420                                                         
      IF(M1 .NE. 0) JUNIT(2) = M1                                       RDN 1430                                                         
      CALL CHECK(P(K),JUNIT(1),1)                                       RDN 1440                                                         
      CALL CHECK(T(K),JUNIT(2),2)                                       RDN 1450                                                         
      CALL DEFALT(ZMDL(K),P(K),T(K))                                    RDN 1460                                                         
      CALL CONVRT (P(K),T(K) )                                          RDN 1470                                                         
      DO 20 KM = 1,12                                                   RDN 1480                                                         
20    WMDL(K,KM) = WMOL(KM)                                             RDN 1490                                                         
      WMDL(K,13) = WAIR1                                                RDN 1500                                                         
200   CONTINUE                                                          RDN 1510                                                         
      RETURN                                                            RDN 1520                                                         
      END                                                               RDN 1530                                                         
      SUBROUTINE DEFALT  (Z,P,T)                                        DEF  100                                                         
C                                                                       DEF  110                                                         
C     ******************************************************************DEF  120                                                         
C                                                                       DEF  130                                                         
C     THIS SUBROUTINE LOADS ONE OF THE 6 BUILT IN ATMOSPHERIC PROFILES  DEF  140                                                         
C     FROM WHICH IT WILL INTERPOLATE "DEFAULT" VALUES FOR ALTITUDE "Z"  DEF  150                                                         
C                                                                       DEF  160                                                         
C                                                                       DEF  170                                                         
C      ***  THIS SUBROUTINE IS CALLED BY "RDUNIT" WHICH                 DEF  180                                                         
C      ***  READS USER SUPPLIED INPUT PROFILES OR SINGLE VALUES         DEF  190                                                         
C      ***  UNDER "MODEL = 0     " SPECIFICATIONS                       DEF  200                                                         
C                                                                       DEF  210                                                         
C      *** SEE DOCUMENTATION FOR CLARIFICATION ***                      DEF  220                                                         
C                                                                       DEF  230                                                         
C     SUBROUTINE "DEFALT"IS TRIGGERRED WHENEVER ANY ONE OF              DEF  240                                                         
