C                                                                       DEF  260                                                         
C     FOR SIMPLICITY, ALL INTERPOLATIONS ARE DONE AT ONE TIME BECAUSE   DEF  270                                                         
C     THE LAGRANGE WEIGHTS (4PT), BASED ON (ALT-Z), REMAIN UNCHANGED    DEF  280                                                         
C                                                                       DEF  290                                                         
C                   JCHAR(K) FOR K<8 ALLOW MODEL-DEPENDENT CHOICES      DEF  300                                                         
C                                                                       DEF  310                                                         
C                   JCHAR=JUNIT                                         DEF  320                                                         
C                                                                       DEF  330                                                         
C                        1       CHOOSES TROPICAL                       DEF  340                                                         
C                        2         "     MID-LATITUDE SUMMER            DEF  350                                                         
C                        3         "     MID-LATITUDE WINTER            DEF  360                                                         
C                        4         "     HIGH-LAT SUMMER                DEF  370                                                         
C                        5         "     HIGH-LAT WINTER                DEF  380                                                         
C                        6         "     US STANDARD                    DEF  390                                                         
C                                                                       DEF  400                                                         
C                                                                       DEF  410                                                         
C    JUNIT(K) FOR K>7 CHOOSES FROM THE SINGLE TRACE CONSTITUENT         DEF  420                                                         
C        PROFILES, ALL APPRORIATE FOR THE US STD ATMOSPHERE             DEF  430                                                         
C                                                                       DEF  440                                                         
C     ***  NOTE ***  T<0 WILL ALSO PRINT OUT A MESSAGE INDICATING       DEF  450                                                         
C     ***  A POSSIBLE MISAPPLICATION OF TEMPERATURE UNITS, (K) VS (C)   DEF  460                                                         
C                                                                       DEF  470                                                         
C     ******************************************************************DEF  480                                                         
      COMMON /IFIL/IRD,IPR,IPU,NPR,IPR1                                 DEF  490                                                         
      COMMON /CARD1B/ JUNITP,JUNITT,JUNIT(13),WMOL(12),WAIR,JLOW        DEF  500                                                         
      COMMON /MLATM / ALT(50),PMATM(50,6),TMATM(50,6),AMOL(50,8,6)      DEF  510                                                         
      COMMON /TRAC/ TRAC(50,21)                                         DEF  520                                                         
C                                                                       DEF  530                                                         
      DATA PZERO /1013.25/,TZERO/273.15/,XLOSCH/2.6868E19/              DEF  540                                                         
C                                                                       DEF  550                                                         
C     *** 4PT INTERPOLATION FUNCTION                                    DEF  560                                                         
C                                                                       DEF  570                                                         
      VAL(A1,A2,A3,A4,X1,X2,X3,X4)=A1*X1+A2*X2+A3*X3+A4*X4              DEF  580                                                         
C                                                                       DEF  590                                                         
C                                                                       DEF  600                                                         
      NMOL = 12                                                         DEF  610                                                         
      ILOWER=0                                                          DEF  620                                                         
      IUPPER=0                                                          DEF  630                                                         
      IM50=50                                                           DEF  640                                                         
      DO 15 IM=2,IM50                                                   DEF  650                                                         
      I2=IM                                                             DEF  660                                                         
      IF (ALT(IM).GE.Z) GO TO 20                                        DEF  670                                                         
   15 CONTINUE                                                          DEF  680                                                         
      I2=IM50                                                           DEF  690                                                         
   20 I1=I2-1                                                           DEF  700                                                         
      I0=I2-2                                                           DEF  710                                                         
      I3=I2+1                                                           DEF  720                                                         
      IF(I0.LT.1) GO TO 25                                              DEF  730                                                         
      IF(I3.GT.IM50) GO TO 26                                           DEF  740                                                         
C                                                                       DEF  750                                                         
      GO TO 28                                                          DEF  760                                                         
C                                                                       DEF  770                                                         
C     LOWER ENDPOINT CORRECTION                                         DEF  780                                                         
C                                                                       DEF  790                                                         
25    CONTINUE                                                          DEF  800                                                         
      ILOWER=1                                                          DEF  810                                                         
      I0=I1                                                             DEF  820                                                         
      I1=I2                                                             DEF  830                                                         
      I2=I3                                                             DEF  840                                                         
      I3=I3+1                                                           DEF  850                                                         
      GO TO 28                                                          DEF  860                                                         
C                                                                       DEF  870                                                         
C     UPPER ENDPOINT CORRECTION                                         DEF  880                                                         
C                                                                       DEF  890                                                         
26    CONTINUE                                                          DEF  900                                                         
      IUPPER=1                                                          DEF  910                                                         
      IF(Z.GT.ALT(IM50))GO TO 29                                        DEF  920                                                         
      I3=I2                                                             DEF  930                                                         
      I2=I1                                                             DEF  940                                                         
      I1=I0                                                             DEF  950                                                         
      I0=I1-1                                                           DEF  960                                                         
      GO TO 28                                                          DEF  970                                                         
C                                                                       DEF  980                                                         
C      UPPER ENDPOINT EXTRAPOLATION                                     DEF  990                                                         
C                                                                       DEF 1000                                                         
29    CONTINUE                                                          DEF 1010                                                         
      Z0=ALT(I0)                                                        DEF 1020                                                         
      Z1=ALT(I1)                                                        DEF 1030                                                         
      Z2=ALT(I2)                                                        DEF 1040                                                         
      Z3=Z2+2.*(Z-Z2)                                                   DEF 1050                                                         
      IUPPER=2                                                          DEF 1060                                                         
      WRITE(IPR,80)Z                                                    DEF 1070                                                         
80    FORMAT(/,'   *** Z IS GREATER THAN 120 KM ***, Z = ',F10.3)       DEF 1080                                                         
      STOP'DEFAULTZ'                                                    DEF 1090                                                         
C     I3=I2                                                             DEF 1100                                                         
C     GO TO 31                                                          DEF 1110                                                         
C                                                                       DEF 1120                                                         
C     LAGRANGE CONTINUATION                                             DEF 1130                                                         
C                                                                       DEF 1140                                                         
28    CONTINUE                                                          DEF 1150                                                         
C                                                                       DEF 1160                                                         
C      LAGRANGE COEF DETERMINATION                                      DEF 1170                                                         
C                                                                       DEF 1180                                                         
      Z1=ALT(I1)                                                        DEF 1190                                                         
      Z2=ALT(I2)                                                        DEF 1200                                                         
      Z0=ALT(I0)                                                        DEF 1210                                                         
      Z3=ALT(I3)                                                        DEF 1220                                                         
31    CONTINUE                                                          DEF 1230                                                         
      DEN1=(Z0-Z1)*(Z0-Z2)*(Z0-Z3)                                      DEF 1240                                                         
      DEN2=(Z1-Z2)*(Z1-Z3)*(Z1-Z0)                                      DEF 1250                                                         
      DEN3=(Z2-Z3)*(Z2-Z0)*(Z2-Z1)                                      DEF 1260                                                         
      DEN4=(Z3-Z0)*(Z3-Z1)*(Z3-Z2)                                      DEF 1270                                                         
      A1=((Z-Z1)*(Z-Z2)*(Z-Z3))/DEN1                                    DEF 1280                                                         
      A2=((Z-Z2)*(Z-Z3)*(Z-Z0))/DEN2                                    DEF 1290                                                         
      A3=((Z-Z3)*(Z-Z0)*(Z-Z1))/DEN3                                    DEF 1300                                                         
      A4=((Z-Z0)*(Z-Z1)*(Z-Z2))/DEN4                                    DEF 1310                                                         
C                                                                       DEF 1320                                                         
C                                                                       DEF 1330                                                         
C     TEST INPUT PARAMETERS (JUNIT'S) SEQUENTIALLY FOR TRIGGER          DEF 1340                                                         
C      I.E.  JUNIT(P,T,K) = 1-6                                         DEF 1350                                                         
C                                                                       DEF 1360                                                         
      IF (JUNITP.GT.6) GO TO 35                                         DEF 1370                                                         
      MATM=JUNITP                                                       DEF 1380                                                         
C     WRITE (IPR,60) Z,MATM                                             DEF 1390                                                         
      X1=ALOG(PMATM(I0,MATM))                                           DEF 1400                                                         
      X2=ALOG(PMATM(I1,MATM))                                           DEF 1410                                                         
      X3=ALOG(PMATM(I2,MATM))                                           DEF 1420                                                         
      X4=ALOG(PMATM(I3,MATM))                                           DEF 1430                                                         
      IF(IUPPER.EQ.2)X4=X3+2*(X3-X2)                                    DEF 1440                                                         
      P=VAL(A1,A2,A3,A4,X1,X2,X3,X4)                                    DEF 1450                                                         
      P=EXP(P)                                                          DEF 1460                                                         
   35 IF (JUNITT.GT.6) GO TO 40                                         DEF 1470                                                         
      MATM=JUNITT                                                       DEF 1480                                                         
C     WRITE (IPR,65) Z,MATM                                             DEF 1490                                                         
      X1=TMATM(I0,MATM)                                                 DEF 1500                                                         
      X2=TMATM(I1,MATM)                                                 DEF 1510                                                         
      X3=TMATM(I2,MATM)                                                 DEF 1520                                                         
      X4=TMATM(I3,MATM)                                                 DEF 1530                                                         
      T=VAL(A1,A2,A3,A4,X1,X2,X3,X4)                                    DEF 1540                                                         
  40  DO 55 K=1,NMOL                                                    DEF 1550                                                         
      IF (JUNIT(K).GT.6) GO TO 55                                       DEF 1560                                                         
C                                                                       DEF 1570                                                         
      IF (K.GT.7) GO TO 45                                              DEF 1580                                                         
      MATM=JUNIT(K)                                                     DEF 1590                                                         
C                                                                       DEF 1600                                                         
      X1=AMOL(I0,K,MATM)                                                DEF 1610                                                         
      X2=AMOL(I1,K,MATM)                                                DEF 1620                                                         
      X3=AMOL(I2,K,MATM)                                                DEF 1630                                                         
      X4=AMOL(I3,K,MATM)                                                DEF 1640                                                         
      GO TO 50                                                          DEF 1650                                                         
   45 ITR=K-7                                                           DEF 1660                                                         
      MATM=6                                                            DEF 1670                                                         
C                                                                       DEF 1680                                                         
      X1=TRAC(I0,ITR)                                                   DEF 1690                                                         
      X2=TRAC(I1,ITR)                                                   DEF 1700                                                         
      X3=TRAC(I2,ITR)                                                   DEF 1710                                                         
      X4=TRAC(I3,ITR)                                                   DEF 1720                                                         
   50 WMOL(K)=VAL(A1,A2,A3,A4,X1,X2,X3,X4)                              DEF 1730                                                         
      JUNIT(K)=10                                                       DEF 1740                                                         
      GO TO 55                                                          DEF 1750                                                         
C  53 JUNIT(K)=10                                                       DEF 1760                                                         
C     WRITE(IPR,54)K                                                    DEF 1770                                                         
C  54 FORMAT('  **** INCONSISTENCY IN THE USER SPECIFICATION',          DEF 1780                                                         
C    A ' , JUNIT = 9 AND WMOL(K) = 0 , K =',I2,/,                       DEF 1790                                                         
C    B '  ****   DENNUM(K) HAS BEEN SET TO 0, NOT DEFAULT VALUE')       DEF 1800                                                         
   55 CONTINUE                                                          DEF 1810                                                         
      WMOL(12)   =WMOL(12)   *1.0E+3                                    DEF 1820                                                         
C     THE UNIT FOR NEW PROFILE IS PPMV.                                 DEF 1830                                                         
      RETURN                                                            DEF 1840                                                         
C100  CONTINUE                                                          DEF 1850                                                         
  110 FORMAT(/,'  *** FATAL INPUT ERROR ***',/,                         DEF 1860                                                         
     A '  DEFAULT SPECIFICATIONS EXCEED MODEL = 6',/,                   DEF 1870                                                         
     B  0PF10.2,' KM',1PE10.3,' MB',0PF10.3,' K',/,                     DEF 1880                                                         
     C (5(I5,1PE10.3)))                                                 DEF 1890                                                         
C     STOP'DEFAULT'                                                     DEF 1900                                                         
C                                                                       DEF 1910                                                         
   60 FORMAT ('   DEFAULT PRESSURE AT Z=',F7.2,' KM FROM MODEL',I5)     DEF 1920                                                         
   65 FORMAT ('   DEFAULT TEMPERATURE AT Z=',F7.2,' KM FROM MODEL',I5)  DEF 1930                                                         
   70 FORMAT ('   DEFAULT,K =',I3,',',A10,',','AT Z=',F7.2,             DEF 1940                                                         
     A ' KM FROM MODEL',I5)                                             DEF 1950                                                         
      END                                                               DEF 1960                                                         
      SUBROUTINE AERPRF (I,  VIS,HAZE,IHAZE,     ISEASN,IVULCN,N)       AER  100                                                         
C***********************************************************************AER  110                                                         
C     WILL COMPUTE DENSITY    PROFILES FOR AEROSOLS                     AER  120                                                         
C***********************************************************************AER  130                                                         
      COMMON/PRFD  /ZHT(34),HZ2K(34,5),FAWI50(34),FAWI23(34),SPSU50(34),AER  140                                                         
     1SPSU23(34),BASTFW(34),VUMOFW(34),HIVUFW(34),EXVUFW(34),BASTSS(34),AER  150                                                         
     2VUMOSS(34),HIVUSS(34),EXVUSS(34),UPNATM(34),VUTONO(34),           AER  160                                                         
     3VUTOEX(34),EXUPAT(34)                                             AER  170                                                         
      DIMENSION VS(5)                                                   AER  180                                                         
      DATA VS/50.,23.,10.,5.,2./                                        AER  190                                                         
      HAZE=0.                                                           AER  200                                                         
      N=7                                                               AER  210                                                         
      IF (IHAZE.EQ.0) RETURN                                            AER  220                                                         
      IF (ZHT(I).GT.2.0) GO TO 15                                       AER  230                                                         
      DO 5 J=2,5                                                        AER  240                                                         
      IF (VIS.GE.VS(J)) GO TO 10                                        AER  250                                                         
    5 CONTINUE                                                          AER  260                                                         
      J=5                                                               AER  270                                                         
   10 CONST=1./(1./VS(J)-1./VS(J-1))                                    AER  280                                                         
      HAZE=CONST*( (HZ2K(I,J)-HZ2K(I,J-1))/VIS +                        AER  290                                                         
     1 HZ2K(I,J-1)/VS(J) - HZ2K(I,J )/VS(J-1) )                         AER  300                                                         
      IF(ZHT(I).GT.2.0) GO TO 15                                        AER  310                                                         
      RETURN                                                            AER  320                                                         
   15 IF (ZHT(I).GT.10.) GO TO 35                                       AER  330                                                         
      CONST=1./(1./23.-1./50.)                                          AER  340                                                         
      IF (ISEASN.GT.1) GO TO 25                                         AER  350                                                         
      IF (VIS.LE.23.) HAZI=SPSU23(I)                                    AER  360                                                         
      IF (VIS.LE.23.) GO TO 200                                         AER  370                                                         
      IF (ZHT(I).GT.4.0) GO TO 20                                       AER  380                                                         
      HAZI=CONST*((SPSU23(I)-SPSU50(I))/VIS+SPSU50(I)/23.-SPSU23(I)/50.)AER  390                                                         
      GO TO 200                                                         AER  400                                                         
   20 HAZI=SPSU50(I)                                                    AER  410                                                         
      GO TO 200                                                         AER  420                                                         
   25 IF (VIS.LE.23.) HAZI=FAWI23(I)                                    AER  430                                                         
      IF (VIS.LE.23.) GO TO 200                                         AER  440                                                         
      IF (ZHT(I).GT.4.0) GO TO 30                                       AER  450                                                         
      HAZI=CONST*((FAWI23(I)-FAWI50(I))/VIS+FAWI50(I)/23.-FAWI23(I)/50.)AER  460                                                         
      GO TO 200                                                         AER  470                                                         
   30 HAZI=FAWI50(I)                                                    AER  480                                                         
      GO TO 200                                                         AER  490                                                         
   35 IF (ZHT(I).GT.30.0) GO TO 75                                      AER  500                                                         
      HAZI=BASTSS(I)                                                    AER  510                                                         
      IF (ISEASN.GT.1) GO TO 55                                         AER  520                                                         
      IF (IVULCN.EQ.0) HAZI=BASTSS(I)                                   AER  530                                                         
      IF (IVULCN.EQ.0) GO TO 200                                        AER  540                                                         
      GO TO (40,45,50,50,45,45,50,52), IVULCN                           AER  550                                                         
   40 HAZI=BASTSS(I)                                                    AER  560                                                         
      GO TO 200                                                         AER  570                                                         
   45 HAZI=VUMOSS(I)                                                    AER  580                                                         
      GO TO 200                                                         AER  590                                                         
   50 HAZI=HIVUSS(I)                                                    AER  600                                                         
      GO TO 200                                                         AER  610                                                         
52    HAZI=EXVUSS(I)                                                    AER  620                                                         
      GO TO 200                                                         AER  630                                                         
   55 IF (IVULCN.EQ.0) HAZI=BASTFW(I)                                   AER  640                                                         
      IF (IVULCN.EQ.0) GO TO 200                                        AER  650                                                         
      GO TO (60,65,70,70,65,65,70,72), IVULCN                           AER  660                                                         
   60 HAZI=BASTFW(I)                                                    AER  670                                                         
      GO TO 200                                                         AER  680                                                         
   65 HAZI=VUMOFW(I)                                                    AER  690                                                         
      GO TO 200                                                         AER  700                                                         
   70 HAZI=HIVUFW(I)                                                    AER  710                                                         
      GO TO 200                                                         AER  720                                                         
72    HAZI=EXVUFW(I)                                                    AER  730                                                         
      GO TO 200                                                         AER  740                                                         
   75 N=14                                                              AER  750                                                         
      IF (IVULCN.GT.1) GO TO 80                                         AER  760                                                         
      HAZI=UPNATM(I)                                                    AER  770                                                         
      GO TO 200                                                         AER  780                                                         
   80 HAZI=VUTONO(I)                                                    AER  790                                                         
200   IF(HAZI.GT.0) HAZE=HAZI                                           AER  800                                                         
      RETURN                                                            AER  810                                                         
      END                                                               AER  820                                                         
      SUBROUTINE GEO(IERROR,BENDNG,MAXGEO)                              GEO  100                                                         
