      GM1=(7.0-3.0*G**2.-OME0*(4.0+3.0*G)+OME0*G**2.*(4.0*B0+3.0*G))/Q  FLX 3180                                                         
      GM2=-(1.0-G**2.-OME0*(4.0-3.0*G)-OME0*G**2.*(4.0*B0+3.0*G-4.0))/Q FLX 3190                                                         
      IF(GM2  .EQ. 0.0) THEN                                            FLX 3200                                                         
          GM2=1.0E-10                                                   FLX 3210                                                         
      ENDIF                                                             FLX 3220                                                         
      SK=SQRT(GM1*GM1-GM2*GM2)                                          FLX 3230                                                         
      CS=           OME0*(BETS(N)/SMU-GM1*BETS(N)-GM2*                  FLX 3240                                                         
     2 (1.-BETS(N)))*(SMU*SMU/(1.-SK*SK*SMU*SMU))                       FLX 3250                                                         
      YS=CS*(GM1+1./SMU)-           OME0*BETS(N)                        FLX 3260                                                         
CC    E1=YS/(GM1-SK)                                                    FLX 3270                                                         
CC    E2=-CS                                                            FLX 3280                                                         
CC    E3=(GM1+SK)/(SK-GM1)                                              FLX 3290                                                         
CC    E4=1.                                                             FLX 3300                                                         
CCC   BS=(E1*EXP(SK*X)+E2*EXP(-X/SMU))/(E3*EXP(SK*X)+E4*EXP(-SK*X))     FLX 3310                                                         
CCC   AS=(BS*(GM1+SK)+YS)/(SK-GM1)                                      FLX 3320                                                         
C                                                                       FLX 3330                                                         
C    CALCULATE UPWARD & DOWNWARD FLUX, EACH LAYER ALONE                 FLX 3340                                                         
C                                                                       FLX 3350                                                         
