      SUBROUTINE O3UV(V,C)                                              O3V  100                                                         
      COMMON /O3UVF/ V1 ,V2 ,DV ,NPT ,S(133)                            O3V  110                                                         
C                                                                       O3V  120                                                         
C     INTERPOLATION  FOR  O3 CONTINUUM WITH LOWTRAN                     O3V  130                                                         
C                                                                       O3V  140                                                         
      C    =0.                                                          O3V  150                                                         
      I=(V  -V1)/DV+1.00001                                             O3V  160                                                         
      IF(I.LT.1   )GO TO 10                                             O3V  170                                                         
      IF(I.GT.NPT )GO TO 10                                             O3V  180                                                         
      VR = I*DV + V1                                                    O3V  190                                                         
      IF(VR. LE. (V+.1) .AND .VR.GE. (V-.1)) GO TO 5                    O3V  200                                                         
      IF(I .EQ. NPT ) I=NPT-1                                           O3V  210                                                         
      AM = (S(I+1) -S(I))/DV                                            O3V  220                                                         
      C0 = S(I) - AM * VR                                               O3V  230                                                         
      C  = AM * V + C0                                                  O3V  240                                                         
      GO TO 10                                                          O3V  250                                                         
5     C    =    S(I)                                                    O3V  260                                                         
10    CONTINUE                                                          O3V  270                                                         
C                                                                       O3V  280                                                         
      RETURN                                                            O3V  290                                                         
      END                                                               O3V  300                                                         
