      ASYM(M,N)=CALSYM(N)                                               EXA 2050                                                         
      IF(N.EQ.1) AWCCON(M)=ASLWC                                        EXA 2060                                                         
      GO TO 150                                                         EXA 2070                                                         
135   ABSC(M,N)=CSTABS(N)                                               EXA 2080                                                         
      EXTC(M,N)=CSTEXT(N)                                               EXA 2090                                                         
      ASYM(M,N)=CSTSYM(N)                                               EXA 2100                                                         
      IF(N.EQ.1) AWCCON(M)=STLWC                                        EXA 2110                                                         
      GO TO 150                                                         EXA 2120                                                         
140   ABSC(M,N)=CSCABS(N)                                               EXA 2130                                                         
      EXTC(M,N)=CSCEXT(N)                                               EXA 2140                                                         
      ASYM(M,N)=CSCSYM(N)                                               EXA 2150                                                         
      IF(N.EQ.1) AWCCON(M)=SCLWC                                        EXA 2160                                                         
      GO TO 150                                                         EXA 2170                                                         
145   ABSC(M,N)=CNIABS(N)                                               EXA 2180                                                         
      EXTC(M,N)=CNIEXT(N)                                               EXA 2190                                                         
      ASYM(M,N)=CNISYM(N)                                               EXA 2200                                                         
      IF(N.EQ.1) AWCCON(M)=SNLWC                                        EXA 2210                                                         
150   CONTINUE                                                          EXA 2220                                                         
185   CONTINUE                                                          EXA 2230                                                         
      DO 200 N=1,47                                                     EXA 2240                                                         
      ABSC(5,N)=0.                                                      EXA 2250                                                         
      EXTC(5,N)=0.                                                      EXA 2260                                                         
      ASYM(5,N)=0.                                                      EXA 2270                                                         
      AWCCON(5)=0.                                                      EXA 2280                                                         
      IF(ICLD .EQ. 18) THEN                                             EXA 2290                                                         
           EXTC(5,N)= CI32XT(N)                                         EXA 2300                                                         
           ABSC(5,N)= CI32AB(N)                                         EXA 2310                                                         
