      IF(ICLD .EQ. 19) THEN                                             EXA 2350                                                         
           EXTC(5,N)= CIR2XT(N)                                         EXA 2360                                                         
           ABSC(5,N)= CIR2AB(N)                                         EXA 2370                                                         
           ASYM(5,N)= CIR2G(N)                                          EXA 2380                                                         
           AWCCON(5)=5.811E-2                                           EXA 2390                                                         
