      SUBROUTINE AEREXT (V)                                             AEX  100                                                         
C                                                                       AEX  110                                                         
C     INTERPOLATES AEROSOL EXTINCTION, ABSORPTION, AND ASYMMETRY        AEX  120                                                         
C     COEFFICIENTS FOR THE WAVENUMBER, V.                               AEX  130                                                         
C                                                                       AEX  140                                                         
C     MODIFIED FOR ASYMMETRY  - JAN 1986 (A.E.R. INC.)                  AEX  150                                                         
C                                                                       AEX  160                                                         
      COMMON /CARD1/ MODEL,ITYPE,IEMSCT,M1,M2,M3,IM,NOPRNT,TBOUND,SALB  AEX  170                                                         
      COMMON /CARD2/ IHAZE,ISEASN,IVULCN,ICSTL,ICLD,IVSA,VIS,WSS,WHH,   AEX  180                                                         
     1    RAINRT                                                        AEX  190                                                         
      COMMON /CARD3/ H1,H2,ANGLE,RANGE,BETA,RE,LEN                      AEX  200                                                         
      COMMON /CARD4/ V1,V2,DV                                           AEX  210                                                         
      COMMON /CNTRL/ KMAX,M,IKMAX,NL,ML,IKLO,ISSGEO,IMULT               AEX  220                                                         
      COMMON RELHUM(34),HSTOR(34),ICH(4),VH(17),TX(63),W(63)            AEX  230                                                         
      COMMON             WPATH(68, 63),TBBY(68)                         AEX  240                                                         
      COMMON ABSC(5,47),EXTC(5,47),ASYC(5,47),VX2(47),AWCCON(5)         AEX  250                                                         
      COMMON/MODEL/ ZMDL(34),PM(34),TM(34),RFNDX(34),DENSTY(63,34),     AEX  260                                                         
     1 CLDAMT(34),RRAMT(34),EQLWC(34),HAZEC(34)                         AEX  270                                                         
      COMMON /AER/ EXTV(5),ABSV(5),ASYV(5)                              AEX  280                                                         
      COMMON /AERTM/TAER(5)                                             AEX  290                                                         
C                                                                       AEX  300                                                         
      DO 5 I=1,5                                                        AEX  310                                                         
      EXTV(I)=0.                                                        AEX  320                                                         
      ABSV(I)=0.                                                        AEX  330                                                         
      ASYV(I)=0.                                                        AEX  340                                                         
    5 CONTINUE                                                          AEX  350                                                         
      IF(V.LE.1.0E-5) GO TO 50                                          AEX  360                                                         
      IF(V.LE.33.333) GO TO 30                                          AEX  370                                                         
CCC                                                                     AEX  380                                                         
CCC   COMPUTE INFRARED ATTENUATION COEFFICIENT                          AEX  390                                                         
CCC                                                                     AEX  400                                                         
      IF(V.LE.50.0) THEN                                                AEX  410                                                         
           DO 2 MR=1,5                                                  AEX  420                                                         
           EXTC(MR,47)=GAMFOG(MR,33.333,TAER(MR),AWCCON(MR))            AEX  430                                                         
           ABSC(MR,47)=EXTC(MR,47)                                      AEX  440                                                         
           ASYC(MR,47)= 0.                                              AEX  450                                                         
2          CONTINUE                                                     AEX  460                                                         
      END IF                                                            AEX  470                                                         
CC    IF (IHAZE.EQ.0) RETURN                                            AEX  480                                                         
      V0=V                                                              AEX  490                                                         
      ALAM=1.0E+4/V0                                                    AEX  500                                                         
      DO 10 N=2,47                                                      AEX  510                                                         
      XD=ALAM-VX2(N)                                                    AEX  520                                                         
      IF (XD) 15,10,10                                                  AEX  530                                                         
   10 CONTINUE                                                          AEX  540                                                         
      N=47                                                              AEX  550                                                         
   15 VXD=VX2(N)-VX2(N-1)                                               AEX  560                                                         
      DO 20 I=1,5                                                       AEX  570                                                         
      EXTV(I)=(EXTC(I,N)-EXTC(I,N-1))*XD/VXD+EXTC(I,N)                  AEX  580                                                         
      ABSV(I)=(ABSC(I,N)-ABSC(I,N-1))*XD/VXD+ABSC(I,N)                  AEX  590                                                         
      ASYV(I)=(ASYC(I,N)-ASYC(I,N-1))*XD/VXD+ASYC(I,N)                  AEX  600                                                         
   20 CONTINUE                                                          AEX  610                                                         
      RETURN                                                            AEX  620                                                         
CCC                                                                     AEX  630                                                         
30    CONTINUE                                                          AEX  640                                                         
CCC    COMPUTE MICROWAVE ATTENUATION COEFFICIENTS                       AEX  650                                                         
CCC                                                                     AEX  660                                                         
      DO 40 I=1,5                                                       AEX  670                                                         
      EXTV(I)=GAMFOG( I,V,TAER(I),AWCCON(I))                            AEX* 680                                                         
      ABSV(I)=EXTV(I)                                                   AEX  690                                                         
      ASYV(I)=0.                                                        AEX  700                                                         
40    CONTINUE                                                          AEX  710                                                         
      RETURN                                                            AEX  720                                                         
CCC                                                                     AEX  730                                                         
C     V LE 1.0E-5                                                       AEX  740                                                         
50    CONTINUE                                                          AEX  750                                                         
CCC                                                                     AEX  760                                                         
CCC   EQL=EQLWC(IL)                                                     AEX  770                                                         
      DO 60 I=1,5                                                       AEX  780                                                         
      EXTV(I)=0.0                                                       AEX  790                                                         
      ABSV(I)=0.                                                        AEX  800                                                         
      ASYV(I)=0.                                                        AEX  810                                                         
C     WRITE (6,300) I,AWCCON(I)                                         AEX  820                                                         
300   FORMAT(5X,'I,AWCCON=',I5,5X,1PE12.5)                              AEX  830                                                         
60    CONTINUE                                                          AEX  840                                                         
100   FORMAT(5X,'IL,IK,EQL,EXTV=',2I5,1P5E12.5)                         AEX  850                                                         
      RETURN                                                            AEX  860                                                         
      END                                                               AEX  870                                                         
      SUBROUTINE AERTMP                                                 AET  100                                                         
      COMMON RELHUM(34),HSTOR(34),ICH(4),VH(17),TX(63),W(63)            AET  110                                                         
      COMMON             WPATH(68, 63),TBBY(68)                         AET  120                                                         
      COMMON ABSC(5,47),EXTC(5,47),ASYM(5,47),VX2(47),AWCCON(5)         AET  130                                                         
      COMMON /IFIL/ IRD,IPR,IPU,NPR,IPR1                                AET  140                                                         
      COMMON /CNTRL/ KMAX,M,IKMAX,NL,ML,IKLO,ISSGEO,IMULT               AET  150                                                         
      COMMON /AERTM/TAE7,TAE12,TAE13,TAE14,TAE16                        AET  160                                                         
      SUM7  = 0.                                                        AET  170                                                         
      SUM12 = 0.                                                        AET  180                                                         
      SUM13 = 0.                                                        AET  190                                                         
      SUM14 = 0.                                                        AET  200                                                         
      SUM16 = 0.                                                        AET  210                                                         
      TAE7  = 0.                                                        AET  220                                                         
      TAE12 = 0.                                                        AET  230                                                         
      TAE13 = 0.                                                        AET  240                                                         
      TAE14 = 0.                                                        AET  250                                                         
      TAE16 = 0.                                                        AET  260                                                         
      DO 10 KK = 1,IKMAX                                                AET  270                                                         
      IF(KK .EQ. 1) THEN                                                AET  280                                                         
           WDF7  = WPATH(KK,7)                                          AET  290                                                         
           WDF12 = WPATH(KK,12)                                         AET  300                                                         
           WDF13 = WPATH(KK,13)                                         AET  310                                                         
           WDF14 = WPATH(KK,14)                                         AET  320                                                         
           WDF16 = WPATH(KK,16)                                         AET  330                                                         
      ELSE                                                              AET  340                                                         
           WDF7  = WPATH(KK,7) -WPATH(KK-1,7)                           AET  350                                                         
           WDF12 = WPATH(KK,12)-WPATH(KK-1,12)                          AET  360                                                         
           WDF13 = WPATH(KK,13)-WPATH(KK-1,13)                          AET  370                                                         
           WDF14 = WPATH(KK,14)-WPATH(KK-1,14)                          AET  380                                                         
           WDF16 = WPATH(KK,16)-WPATH(KK-1,16)                          AET  390                                                         
      ENDIF                                                             AET  400                                                         
      SUM7 = SUM7  + WDF7                                               AET  410                                                         
      SUM12= SUM12 + WDF12                                              AET  420                                                         
      SUM13= SUM13 + WDF13                                              AET  430                                                         
      SUM14= SUM14 + WDF14                                              AET  440                                                         
      SUM16= SUM16 + WDF16                                              AET  450                                                         
      TAE7 = TAE7  + WDF7   * TBBY(KK)                                  AET  460                                                         
      TAE12= TAE12 + WDF12  * TBBY(KK)                                  AET  470                                                         
      TAE13= TAE13 + WDF13  * TBBY(KK)                                  AET  480                                                         
      TAE14= TAE14 + WDF14  * TBBY(KK)                                  AET  490                                                         
10    TAE16= TAE16 + WDF16  * TBBY(KK)                                  AET  500                                                         
      IF(SUM7   .GT. 0.) THEN                                           AET  510                                                         
            TAE7 = TAE7 /SUM7                                           AET  520                                                         
      ELSE                                                              AET  530                                                         
            TAE7 = TBBY(1)                                              AET  540                                                         
      ENDIF                                                             AET  550                                                         
      IF(SUM12 .GT. 0.) THEN                                            AET  560                                                         
            TAE12= TAE12/SUM12                                          AET  570                                                         
      ELSE                                                              AET  580                                                         
            TAE12= TBBY(1)                                              AET  590                                                         
      ENDIF                                                             AET  600                                                         
      IF(SUM13 .GT. 0.) THEN                                            AET  610                                                         
            TAE13= TAE13/SUM13                                          AET  620                                                         
      ELSE                                                              AET  630                                                         
            TAE13= TBBY(1)                                              AET  640                                                         
      ENDIF                                                             AET  650                                                         
      IF(SUM14 .GT. 0.) THEN                                            AET  660                                                         
            TAE14= TAE14/SUM14                                          AET  670                                                         
      ELSE                                                              AET  680                                                         
            TAE14= TBBY(1)                                              AET  690                                                         
      ENDIF                                                             AET  700                                                         
      IF(SUM16 .GT. 0.) THEN                                            AET  710                                                         
            TAE16= TAE16/SUM16                                          AET  720                                                         
      ELSE                                                              AET  730                                                         
            TAE16= TBBY(1)                                              AET  740                                                         
      ENDIF                                                             AET  750                                                         
      RETURN                                                            AET  760                                                         
      END                                                               AET  770                                                         
      SUBROUTINE MARINE(VIS,MODEL,WS,WH,ICSTL,BEXT,BABS,NL)             MAR  100                                                         
C                                                                       MAR  110                                                         
C        THIS SUBROUTINE DETERMINES AEROSOL EXT + ABS COEFFICIENTS      MAR  120                                                         
C          FOR THE NAVY MARITIME MODEL                                  MAR  130                                                         
C            CODED BY STU GATHMAN                  -  NRL               MAR  140                                                         
C                                                                       MAR  150                                                         
C        INPUTS-                                                        MAR  160                                                         
C        WSS = CURRENT WIND SPEED (M/S)                                 MAR  170                                                         
C        WHH = 24 HOUR AVERAGE WIND SPEED (M/S)                         MAR  180                                                         
C        RHH = RELATIVE HUMIDITY (PERCENTAGE)                           MAR  190                                                         
C        VIS = METEOROLOGICAL RANGE (KM)                                MAR  200                                                         
C        ICTL = AIR MASS CHARACTER  1 = OPEN OCEAN                      MAR  210                                                         
C                      10 = STRONG CONTINENTAL INFLUENCE                MAR  220                                                         
C        MODEL = MODEL ATMOSPHERE                                       MAR  230                                                         
C                                                                       MAR  240                                                         
C        OUTPUTS-                                                       MAR  250                                                         
C        BEXT = EXTINCTION COEFFICIENT (KM-1)                           MAR  260                                                         
C        BABS = ABSORPTION COEFFICIENT (KM-1)                           MAR  270                                                         
C                                                                       MAR  280                                                         
      COMMON /MART/ RHH                                                 MAR  290                                                         
      COMMON /IFIL/IRD,IPR,IPU,NPR,IPR1                                 MAR  300                                                         
      COMMON /CNSTNS/ PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                     MAR  310                                                         
      COMMON/A/T1QEXT(40,4),T2QEXT(40,4),T3QEXT(40,4),                  MAR  320                                                         
     1T1QABS(40,4),T2QABS(40,4),T3QABS(40,4),ALAM(40),AREL(4)           MAR  330                                                         
CC    COMMON/AER/A1, A2, A3        X(5)                                 MAR  340                                                         
      DIMENSION WSPD(8), BEXT(5,47), BABS(5,47)                         MAR  350                                                         
      DIMENSION RHD(8)                                                  MAR  360                                                         
      DATA WSPD/6.9, 4.1, 4.1, 10.29, 6.69, 12.35, 7.2, 6.9/            MAR  370                                                         
      DATA RHD/80., 75.63, 76.2, 77.13, 75.24, 80.53, 45.89, 80./       MAR  380                                                         
      PISC = PI/1000.0                                                  MAR  390                                                         
      WRITE(IPR,890)                                                    MAR  400                                                         
C                                                                       MAR  410                                                         
C     CHECK LIMITS OF MODEL VALIDITY                                    MAR  420                                                         
C                                                                       MAR  430                                                         
      RH = RHH                                                          MAR  440                                                         
      IF(RHH.GT.0.) GO TO 10                                            MAR  450                                                         
      RH=RHD(MODEL+1)                                                   MAR  460                                                         
10    IF(WS.GT.20.0) WS=20.                                             MAR  470                                                         
      IF(WH.GT.20.0) WH = 20.                                           MAR  480                                                         
      IF(RH.GT.98.0) RH = 98.                                           MAR  490                                                         
      IF(RH.LT.50.0.AND.RH.GE.0.0) RH = 50.                             MAR  500                                                         
      IF(ICSTL.LT.1.OR.ICSTL.GT.10) ICSTL = 3                           MAR  510                                                         
C                                                                       MAR  520                                                         
C     FIND SIZE DISTRIBUTION PARAMETERS FROM METEOROLOGY INPUT          MAR  530                                                         
C                                                                       MAR  540                                                         
      IF(WH.LE.0.) WRITE(IPR,920)                                       MAR  550                                                         
      IF(WH .LE. 0.0) WH = WSPD(MODEL + 1)                              MAR  560                                                         
      IF(WS.LE.0.) WRITE(IPR,930)                                       MAR  570                                                         
      IF(WS.LE.0.0)WS=WH                                                MAR  580                                                         
      WRITE(IPR,910)WS,WH,RH,ICSTL                                      MAR  590                                                         
C                                                                       MAR  600                                                         
C        F IS A RELATIVE HUMIDITY DEPENDENT GROWTH CORRECTION           MAR  610                                                         
C        TO THE ATTENUATION COEFFICIENT.                                MAR  620                                                         
C                                                                       MAR  630                                                         
      F=((2.-RH/100.)/(6.*(1.-RH/100.)))**0.33333                       MAR  640                                                         
      A1=2000.0*ICSTL*ICSTL                                             MAR  650                                                         
      A2 = AMAX1(5.866*(WH-2.2), 0.5)                                   MAR  660                                                         
CCC   A3 = AMAX1(0.01527*(WS-2.2), 1.14E-5)                             MAR  670                                                         
      A3 = 10**(0.06*WS-2.8)                                            MAR  680                                                         
C                                                                       MAR  690                                                         
C     FIND EXTINCTION AT 0.55 MICRONS AND NORMALIZE TO 1.               MAR  700                                                         
C                                                                       MAR  710                                                         
C     INTERPOLATE FOR RELATIVE HUMIDITY                                 MAR  720                                                         
C                                                                       MAR  730                                                         
      DO 40   J=2,4                                                     MAR  740                                                         
      IF(RH.LE.AREL(J)) GO TO 42                                        MAR  750                                                         
 40   CONTINUE                                                          MAR  760                                                         
 42   DELRH=AREL(J)-AREL(J-1)                                           MAR  770                                                         
      DELRHV=RH-AREL(J-1)                                               MAR  780                                                         
      RATIO=DELRHV/DELRH                                                MAR  790                                                         
      QE1=T1QEXT(4,J-1)+(T1QEXT(4,J)-T1QEXT(4,J-1))*RATIO               MAR  800                                                         
      QE2=T2QEXT(4,J-1)+(T2QEXT(4,J)-T2QEXT(4,J-1))*RATIO               MAR  810                                                         
      QE3=T3QEXT(4,J-1)+(T3QEXT(4,J)-T3QEXT(4,J-1))*RATIO               MAR  820                                                         
      TOTAL = A1*10.**QE1 + A2*10.**QE2 + A3*10.**QE3                   MAR  830                                                         
      EXT55=PISC*TOTAL/F                                                MAR  840                                                         
C                                                                       MAR  850                                                         
C     IF METEOROLOLICAL RANGE NOT SPECIFIED,FIND FROM METEOR DATA       MAR  860                                                         
C                                                                       MAR  870                                                         
      IF(VIS.LE.0.) VIS=3.912/(EXT55+0.01159)                           MAR  880                                                         
      C=(1./EXT55)*(PISC/F)                                             MAR  890                                                         
      A1=C*A1                                                           MAR  900                                                         
      A2=C*A2                                                           MAR  910                                                         
      A3=C*A3                                                           MAR  920                                                         
C                                                                       MAR  930                                                         
C     CALCULATE NORMALIZED ATTENUATION COEFICIENTS                      MAR  940                                                         
C                                                                       MAR  950                                                         
      DO 45   I=1,40                                                    MAR  960                                                         
      T1XV = T1QEXT(I,J-1) + (T1QEXT(I,J) - T1QEXT(I,J-1))*RATIO        MAR  970                                                         
      T2XV = T2QEXT(I,J-1) + (T2QEXT(I,J) - T2QEXT(I,J-1))*RATIO        MAR  980                                                         
      T3XV = T3QEXT(I,J-1) + (T3QEXT(I,J) - T3QEXT(I,J-1))*RATIO        MAR  990                                                         
      T1AV = T1QABS(I,J-1) + (T1QABS(I,J) - T1QABS(I,J-1))*RATIO        MAR 1000                                                         
      T2AV = T2QABS(I,J-1) + (T2QABS(I,J) - T2QABS(I,J-1))*RATIO        MAR 1010                                                         
      T3AV = T3QABS(I,J-1) + (T3QABS(I,J) - T3QABS(I,J-1))*RATIO        MAR 1020                                                         
      BEXT(NL,I)=A1*10**(T1XV)+A2*10**(T2XV)+A3*10**(T3XV)              MAR 1030                                                         
      BABS(NL,I)=A1*10**(T1AV)+A2*10**(T2AV)+A3*10**(T3AV)              MAR 1040                                                         
 45   CONTINUE                                                          MAR 1050                                                         
      WRITE(IPR,900) VIS                                                MAR 1060                                                         
      RETURN                                                            MAR 1070                                                         
890   FORMAT('0MARINE AEROSOL MODEL USED')                              MAR 1080                                                         
900   FORMAT('0',T10,'VIS = ',F10.2,' KM')                              MAR 1090                                                         
910   FORMAT(T10,'WIND SPEED = ',F8.2,' M/SEC',/,T10,                   MAR 1100                                                         
     1 'WIND SPEED (24 HR AVERAGE) = ',F8.2,' M/SEC',/,                 MAR 1110                                                         
     2 T10,'RELATIVE HUMIDITY = ',F8.2,' PERCENT',/,                    MAR 1120                                                         
     3 T10,'AIRMASS CHARACTER =' ,I3)                                   MAR 1130                                                         
920   FORMAT('0  WS NOT SPECIFIED, A DEFAULT VALUE IS USED')            MAR 1140                                                         
930   FORMAT('0  WH NOT SPECIFIED, A DEFAULT VALUE IS USED')            MAR 1150                                                         
      END                                                               MAR 1160                                                         
