      DATA H2/2.828,4.611,6.755,8.759,10.51,13.74,18.00,21.51,23.09,21.6HNO  190                                                         
     18,21.32,16.82,16.42,17.87,14.86,8.716/                            HNO  200                                                         
C     ARRAY H3 CONTAINS HNO3 ABS, COEF(CM-1ATM-1) FROM 1675 TO1735 CM-1 HNO  210                                                         
      DATA H3/5.003,8.803,14.12,19.83,23.31,23.58,23.22,21.09,26.99,25.8HNO  220                                                         
     14,24.79,17.68,9.420/                                              HNO  230                                                         
      HABS=0.                                                           HNO  240                                                         
      IF (V.GE.850.0.AND.V.LE.920.0) GO TO 5                            HNO  250                                                         
      IF (V.GE.1275.0.AND.V.LE.1350.0) GO TO 10                         HNO  260                                                         
