      FUNCTION   SUN(V)                                                 SUN  100                                                         
C                                                                       SUN  110                                                         
C        EVALUATES THE EXTRA-TERRESTRIAL SOLAR IRRADIANCE               SUN  120                                                         
C                                                                       SUN  130                                                         
C        INPUT:  V  =  FREQUENCY   (CM-1)                               SUN  140                                                         
C                VALID RANGE   0 TO 57490 (CM-1)                        SUN  150                                                         
C                    (EQUIVALENT TO WAVELENGTHS > 0.174 MICROMETERS)    SUN  160                                                         
C                                                                       SUN  170                                                         
C        OUTPUT:  SUN  =  SOLAR IRRADIANCE  (WATTS M-2 MICROMETER-1)    SUN  180                                                         
C                                                                       SUN  190                                                         
C        WRITES A WARNING MESSAGE TO TAPE6  &  RETURNS  SUN = 0         SUN  200                                                         
C            IF THE INPUT FREQUENCY IS OUT OF RANGE                     SUN  210                                                         
C                                                                       SUN  220                                                         
C       USES  BLOCK DATA SOLAR   WHICH CONTAINS THE VALUES FOR SOLARA + SUN  230                                                         
C                                                                       SUN  240                                                         
      COMMON /SUNDAT/ SOLARA(1440), SOLARB(2910)                        SUN  250                                                         
      DATA  A, B / 3.50187E-13, 3.93281 /                               SUN  260                                                         
C                                                                       SUN  270                                                         
C       WM, W0, W1, W2  ARE STATEMENT FUNCTIONS USED BY                 SUN  280                                                         
C            THE 4 POINT LAGRANGE INTERPOLATION                         SUN  290                                                         
      WM(P) = P*(P - 1)*(P - 2)                                         SUN  300                                                         
      W0(P) = 3*(P**2 - 1)*(P - 2)                                      SUN  310                                                         
      W1(P) = 3*P*(P + 1)*(P - 2)                                       SUN  320                                                         
      W2(P) = P*(P**2 - 1)                                              SUN  330                                                         
C                                                                       SUN  340                                                         
C           IF  V  IS TOO SMALL,  WRITE WARNING  +  RETURN SUN = 0      SUN  350                                                         
      IF(V .LT. 0.0) THEN                                               SUN  360                                                         
        SUN = 0.0                                                       SUN  370                                                         
        WRITE(6, 900) V                                                 SUN  380                                                         
        RETURN                                                          SUN  390                                                         
C                                                                       SUN  400                                                         
      ELSEIF( V .GE. 0.0  .AND.  V .LT. 100.0 ) THEN                    SUN  410                                                         
C         FOR LOW FREQUENCIES USE A POWER LAW APPROXIMATION             SUN  420                                                         
        SUN = A*V**B                                                    SUN  430                                                         
        RETURN                                                          SUN  440                                                         
C                                                                       SUN  450                                                         
      ELSEIF( V .GE. 100.0  .AND.  V .LT. 28420.0 ) THEN                SUN  460                                                         
C         USE  4 POINT INTERPOLATION  ON  ARRAY  SOLARA                 SUN  470                                                         
C               WHICH IS AT  20 CM-1  SPACING  FROM 0 TO 28720 CM-1     SUN  480                                                         
        I = 1 + INT(V/20.0)                                             SUN  490                                                         
        P = MOD(V, 20.0)/20.0                                           SUN  500                                                         
        SUN = ( W2(P)*SOLARA(I+2) - W1(P)*SOLARA(I+1) +                 SUN  510                                                         
     +              W0(P)*SOLARA(I) - WM(P)*SOLARA(I-1) ) / 6           SUN  520                                                         
        RETURN                                                          SUN  530                                                         
C                                                                       SUN  540                                                         
      ELSEIF( V .GE. 28420.0  .AND.  V .LE. 57470.0 ) THEN              SUN  550                                                         
C         USE  4 POINT INTERPOLATION  ON  ARRAY  SOLARB                 SUN  560                                                         
C             WHICH IS AT  10 CM-1  SPACING  FROM 28400 TO 57490 CM-1   SUN  570                                                         
        I = INT(V/10.0) - 2839                                          SUN  580                                                         
        P = MOD(V, 10.0)/10.0                                           SUN  590                                                         
        SUN = ( W2(P)*SOLARB(I+2) - W1(P)*SOLARB(I+1) +                 SUN  600                                                         
     +              W0(P)*SOLARB(I) - WM(P)*SOLARB(I-1) ) / 6           SUN  610                                                         
        RETURN                                                          SUN  620                                                         
C                                                                       SUN  630                                                         
      ELSEIF( V .GT. 57470.0 ) THEN                                     SUN  640                                                         
C           IF  V  IS TOO LARGE,  WRITE WARNING  +  RETURN SUN = 0      SUN  650                                                         
        SUN = 0.0                                                       SUN  660                                                         
        WRITE(6, 900) V                                                 SUN  670                                                         
        RETURN                                                          SUN  680                                                         
C                                                                       SUN  690                                                         
      ENDIF                                                             SUN  700                                                         
C                                                                       SUN  710                                                         
      RETURN                                                            SUN  720                                                         
  900 FORMAT('0 *****  WARNING - INPUT FREQUENCY = ', 1PG12.5, 'CM-1',  SUN  730                                                         
     +  /, '   OUTSIDE VALID RANGE OF 0 TO 57470 CM-1    *******', / )  SUN  740                                                         
      END                                                               SUN  750                                                         
