      SUBROUTINE SUBSOL(THETAS,PHIS,TIME,IDAY)                          SUB  100                                                         
C                                                                       SUB  110                                                         
C     SUBROUTINE SUBSOL CALCULATES THE SUBSOLAR POINT ANGLES            SUB  120                                                         
C     THETAS AND PHIS BASED UPON IDAY AND TIME. SINCE EACH              SUB  130                                                         
C     YEAR IS 365.25 DAYS LONG THE EXACT VALUE OF THE DECLINATION       SUB  140                                                         
C     ANGLE CHANGES FROM YEAR TO YEAR.  FOR PRECISE VALUES CONSULT      SUB  150                                                         
C     ' THE AMERICAN EPHEMERIS AND NAUTICAL ALMANAC' PUBLISHED YEARLY   SUB  160                                                         
C     BY THE U.S. GOVT. PRINTING OFFICE.  ALSO, THE SOLAR POSITION      SUB  170                                                         
C     IS CHARACTERIZED BY 25 POINTS BELOW; THIS SHOULD PREDICT THE SUBSOSUB  180                                                         
C     ANGLES WITHIN ONE DEGREE.  FOR INCREASED ACCURACY ADD MORE DATA   SUB  190                                                         
C     POINTS                                                            SUB  200                                                         
C                                                                       SUB  210                                                         
C     THE EQUATION OF TIME, EQT, IS IN MINUTES                          SUB  220                                                         
C     THE DECLINATION ANGLE, DEC IS IN DEGREES                          SUB  230                                                         
C                                                                       SUB  240                                                         
      COMMON /MODEL/ ZM(34),PM(34),TM(34),RFNDX(34),DENSTY(63,34),      SUB  250                                                         
     1 CLDAMT(34),RRAMT(34),EQLWC(34),HAZEC(34)                         SUB  260                                                         
      COMMON /IFIL/ IRD,IPR,IPU,NPR,IPR1                                SUB  270                                                         
      DIMENSION NDAY(25),EQT(25),DEC(25)                                SUB  280                                                         
      DATA NDAY /1,9,21,32,44,60,91,121,141,152,160,172,182,            SUB  290                                                         
     1 190,202,213,244,274,305,309,325,335,343,355,366/                 SUB  300                                                         
      DATA DEC /-23.07,-22.22,-20.08,-17.32,-13.62,-7.88,4.23,          SUB  310                                                         
     1 14.83, 20.03,21.95,22.87,23.45,23.17,22.47,20.63,18.23,8.58,     SUB  320                                                         
     2 -2.88,-14.18,-15.45,-19.75,-21.68,-22.75,-23.43,-23.07/          SUB  330                                                         
      DATA EQT /-3.23,-6.83,-11.17,-13.57,-14.33,-12.63,-4.2,           SUB  340                                                         
     1 2.83,3.57,2.45,1.10,-1.42,-3.52,-4.93,-6.25,-6.28,-0.25,         SUB  350                                                         
     2 10.02,16.35,16.38,14.3,11.27,8.02,2.32,-3.23/                    SUB  360                                                         
      IF(IDAY.LT.1.OR.IDAY.GT.366)  GO TO 900                           SUB  370                                                         
      IF(TIME.LT.0.0.OR.TIME.GT.24.0)  GO TO 910                        SUB  380                                                         
      DO 10 I=1,25                                                      SUB  390                                                         
      IF(NDAY(I).EQ.IDAY) GO TO 30                                      SUB  400                                                         
10    IF(NDAY(I).GT.IDAY) GO TO 20                                      SUB  410                                                         
20    I=I-1                                                             SUB  420                                                         
      EQTIME=EQT(I)+(EQT(I+1)-EQT(I))*(IDAY-NDAY(I))/(NDAY(I+1)-NDAY(I))SUB  430                                                         
      DECANG=DEC(I)+(DEC(I+1)-DEC(I))*(IDAY-NDAY(I))/(NDAY(I+1)-NDAY(I))SUB  440                                                         
      GO TO 40                                                          SUB  450                                                         
30    EQTIME=EQT(I)                                                     SUB  460                                                         
      DECANG=DEC(I)                                                     SUB  470                                                         
40    THETAS=DECANG                                                     SUB  480                                                         
      EQTIME=EQTIME/60.0                                                SUB  490                                                         
      PHIS=15.0*(TIME+EQTIME)-180.0                                     SUB  500                                                         
      IF(PHIS.LT.0.0) PHIS=PHIS+360.0                                   SUB  510                                                         
      RETURN                                                            SUB  520                                                         
900   WRITE(IPR,901) IDAY                                               SUB  530                                                         
901   FORMAT(' FROM SUBSOL - IDAY OUT OF RANGE, IDAY=',I6)              SUB  540                                                         
      STOP                                                              SUB  550                                                         
910   WRITE(IPR,902) TIME                                               SUB  560                                                         
902   FORMAT(' FROM SUBSOL - TIME OUT OF RANGE, TIME=',E12.5)           SUB  570                                                         
      STOP                                                              SUB  580                                                         
      END                                                               SUB  590                                                         
      SUBROUTINE PHASEF(V,IK,ALT,SANGLE,RH,PHFA)                        PHS  100                                                         
C                                                                       PHS  110                                                         
C     RETURNS THE AEROSOL PHASE FUNCTION FROM THE STORED DATA BASE      PHS  120                                                         
C                                                                       PHS  130                                                         
C                                                                       PHS  140                                                         
C     THE TRUTH TABLE MNUM(27,26) STORED IN COMMON/MNMPHS/              PHS  150                                                         
C     IN SUBROUTINE PHSDTA IS QUERIED TO DETERMINE THE PROPER PHASE     PHS  160                                                         
C     FUNCTION NEEDED.                                                  PHS  170                                                         
C     THE 27 POSITIONS REPRESENT THE 27 SPECIFIC FREQUENCIES SHOWN IN   PHS  180                                                         
C     DATA STATEMENT WAVE  .2-40 MICRONS.                               PHS  190                                                         
C     THE NUMBERS STORED IN THESE 27 POSITIONS REPRESENT THE CORRECT    PHS  200                                                         
C     PHASE FUNCTIONS CHOSEN FROM THE DATA STATEMENT PHSFNC'S 1-70      PHS  210                                                         
C     POSSIBLE CHOICES.                                                 PHS  220                                                         
C     THE 26 DATA STATEMENTS EACH HAVING 27 FREQUENCIES REPRESENT THE   PHS  230                                                         
C     FOLLOWING 26 MODELS;                                              PHS  240                                                         
C      1=RURAL     0%RH   2=RURAL    70%RH   3=RURAL    80%RH           PHS  250                                                         
C      4=RURAL    99%RH   5=MARITIME  0%RH   6=MARITIME 70%RH           PHS  260                                                         
C      7=MARITIME 80%RH   8=MARITIME 99%RH   9=URBAN     0%RH           PHS  270                                                         
C     10=URBAN    70%RH  11=URBAN    80%RH  12=URBAN    99%RH           PHS  280                                                         
C     13=OCEANIC   0%RH  14=OCEANIC  70%RH  15=OCEANIC  80%RH           PHS  290                                                         
C     16=OCEANIC  99%RH  17=TROPOSPH  0%RH  18=TROPOSPH 70%RH           PHS  300                                                         
C     19=TROPOSPH 80%RH  20=TROPOSPH 99%RH  21=STRATOSPHERIC            PHS  310                                                         
C     22=AGED VOLCANIC   23=FRESH VOLCANIC  24=RADIATION FOG            PHS  320                                                         
C     25=ADVECTIVE FOG   26=METEORIC DUST                               PHS  330                                                         
C                                                                       PHS  340                                                         
C                                                                       PHS  350                                                         
C     IN THE PRESENT VERSION THE 4 OCEANIC MODELS 13-16                 PHS  360                                                         
C     ARE NOT UTILIZED.                                                 PHS  370                                                         
C                                                                       PHS  380                                                         
