      SUBROUTINE HENGNS(ZZ,SANGLE,PHFA)                                 HEN  100                                                         
C                                                                       HEN  110                                                         
C    CALCULATES THE PHASE FUNCTION USING THE HENREY GREENSTEIN          HEN  120                                                         
C    METHOD FOR CLOUDS, DESERT AERSOL, RAIN OR USER READ IN MODEL       HEN  130                                                         
C                                                                       HEN  140                                                         
      COMMON /CNSTNS/ PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                     HEN  150                                                         
C                                                                       HEN  160                                                         
      PFHG(GG,X)=(1.0-GG**2)/(4.*PI*(1.0+GG**2-2.0*GG*X)**1.5)          HEN  170                                                         
      PHFA = PFHG(ZZ,SANGLE)                                            HEN  180                                                         
      RETURN                                                            HEN  190                                                         
      END                                                               HEN  200                                                         
      FUNCTION   PF(NN,I,J)                                             PF   100                                                         
C     CALL THE APPROPRIATE PHASE FUNCTION                               PF   110                                                         
      COMMON/MNMPHS/ MNUM(27,26),PHSFNC(34,70)                          PF   120                                                         
      M=MNUM(I,NN)                                                      PF   130                                                         
      PF=PHSFNC(J,M)                                                    PF   140                                                         
      RETURN                                                            PF   150                                                         
      END                                                               PF   160                                                         
      SUBROUTINE INTERP(INTYPE,X,X1,X2,F,F1,F2)                         INT  100                                                         
C     SUBROUTINE INTERP INTERPOLATES TO DETERMINE THE VALUE OF F        INT  110                                                         
C     AT X, GIVEN F1 AT X1 AND F2 AT X2.                                INT  120                                                         
C     INTYPE=1 FOR LINEAR INTERPOLATION                                 INT  130                                                         
C     INTYPE=2 FOR LOGARITHMIC INTERPOLATION                            INT  140                                                         
      ITYPE=INTYPE                                                      INT  150                                                         
      IF(F1.LE.0.0.OR.F2.LE.0.0) ITYPE=1                                INT  160                                                         
      IF(ITYPE.EQ.2) GO TO 100                                          INT  170                                                         
C     LINEAR INTERPOLATION                                              INT  180                                                         
      F=F1+(X-X1)*(F2-F1)/(X2-X1)                                       INT  190                                                         
      RETURN                                                            INT  200                                                         
100   CONTINUE                                                          INT  210                                                         
      A1=ALOG(F1)                                                       INT  220                                                         
      A2=ALOG(F2)                                                       INT  230                                                         
      A=A1+(X-X1)*(A2-A1)/(X2-X1)                                       INT  240                                                         
      F=EXP(A)                                                          INT  250                                                         
      RETURN                                                            INT  260                                                         
      END                                                               INT  270                                                         
      FUNCTION   RAYSCT(V)                                              RAY  100                                                         
C     RADIATION FLD OUT                                                 RAY  110                                                         
C **  MOLECULAR SCATTERING                                              RAY  120                                                         
      RAYSCT=0.                                                         RAY  130                                                         
      IF(V.LE.3000.) RETURN                                             RAY  140                                                         
      RAYSCT=V**3/(9.26799E+18-1.07123E+09*V**2)                        RAY  150                                                         
C     V**4 FOR RADIATION FLD IN                                         RAY  160                                                         
      RETURN                                                            RAY  170                                                         
      END                                                               RAY  180                                                         
      SUBROUTINE EXPINT(X,X1,X2,A)                                      EXP  100                                                         
C     EXPONENTIAL INTERPOLATION                                         EXP  110                                                         
      IF(X1.EQ.0.0 .OR. X2.EQ.0.0)  GO TO 100                           EXP  120                                                         
      X = X1*(X2/X1)**A                                                 EXP  130                                                         
      RETURN                                                            EXP  140                                                         
  100 X = X1+(X2-X1)*A                                                  EXP  150                                                         
      RETURN                                                            EXP  160                                                         
      END                                                               EXP  170                                                         
      SUBROUTINE EQULWC                                                 EQU  100                                                         
CCC                                                                     EQU  110                                                         
CCC   EQUIVALENT LIQUID  WATER CONSTANTS FOR BEXT (0.55UM)=1.0KM-1      EQU  120                                                         
CCC   AWCCON(1-4) IS SET TO ONE OF THE CONSTANTS FOR EACH AEROSOL       EQU  130                                                         
CCC   IN SUBROUTINE EXABIN AND MULTIPLIED BY THE BEXT (DENSTY(N,I))     EQU  140                                                         
CCC   WHERE N=7,12,13 OR 14 AND I IS THE NUMBER OF LAYERS               EQU  150                                                         
CCC                                                                     EQU  160                                                         
      COMMON RELHUM(34),HSTOR(34),ICH(4),VH(17),TX(63),W(63)            EQU  170                                                         
      COMMON             WPATH(68, 63),TBBY(68)                         EQU  180                                                         
      COMMON ABSC(5,47),EXTC(5,47),ASYM(5,47),VX2(47),AWCCON(5)         EQU  190                                                         
      COMMON /CNTRL/ KMAX,M,IKMAX,NL,ML,IKLO,ISS,IMULT                  EQU  200                                                         
      COMMON/MODEL/   ZM(34),PM(34),TM(34),RFNDX(34),DENSTY(63,34),     EQU  210                                                         
     1 CLDAMT(34),RRAMT(34),EQLWC(34),HAZEC(34)                         EQU  220                                                         
      DO 140 I=1,ML                                                     EQU  230                                                         
      IF(DENSTY(7,I).NE.0.0) EQLWC(I)=DENSTY(7,I)*AWCCON(1)             EQU  240                                                         
      IF(DENSTY(12,I).NE.0.0) EQLWC(I)=DENSTY(12,I)*AWCCON(2)           EQU  250                                                         
      IF(DENSTY(13,I).NE.0.0) EQLWC(I)=DENSTY(13,I)*AWCCON(3)           EQU  260                                                         
      IF(DENSTY(14,I).NE.0.0) EQLWC(I)=DENSTY(14,I)*AWCCON(4)           EQU  270                                                         
140   CONTINUE                                                          EQU  280                                                         
      RETURN                                                            EQU  290                                                         
      END                                                               EQU  300                                                         
