     2  .848, .927,1.000,1.044,1.079, .922, .956,1.000,1.055,1.111,     GMR 1530                                                         
     3  .950, .976,1.000,1.013,1.036, .932, .966,1.000,1.034,1.079,     GMR 1540                                                         
     4  .932, .966,1.000,1.034,1.068, .957, .978,1.000,1.022,1.044/     GMR 1550                                                         
      DATA FACEQ5/                                                      GMR 1560                                                         
     1 1.606,1.252,1.000, .816, .680,1.603,1.265,1.000, .807, .662,     GMR 1570                                                         
     2  .820, .918,1.000,1.075,1.132, .941, .966,1.000,1.034,1.079,     GMR 1580                                                         
     3  .960, .976,1.000,1.013,1.036, .932, .966,1.000,1.034,1.079,     GMR 1590                                                         
     4  .932, .966,1.000,1.034,1.068, .957, .978,1.000,1.022,1.044/     GMR 1600                                                         
      DATA RATLIM /.05/                                                 GMR 1610                                                         
C         GIVE ZERO ATTN IF RATE FALLS BELOW LIMIT                      GMR 1620                                                         
      IF(RATE.GT.RATLIM) GO TO 12                                       GMR 1630                                                         
      GMRAIN = 0.                                                       GMR 1640                                                         
      RETURN                                                            GMR 1650                                                         
   12 WVLTH =  1.0  /FREQ                                               GMR 1660                                                         
CCC   JMAX=3                                                            GMR 1670                                                         
      JMAX=4                                                            GMR 1680                                                         
CCC   IF(WVLTH.GT.WVLTAB(1)) GO TO      14                              GMR 1690                                                         
CCC   ILOW=0                                                            GMR 1700                                                         
CCC   JMAX=2                                                            GMR 1710                                                         
CCC   GO TO 18                                                          GMR 1720                                                         
CCC   THIS DO LOOP IS 2 LESS THAN NO. OF WVLTAB INPUT                   GMR 1730                                                         
CCC14 DO 15 I=2,25                                                      GMR 1740                                                         
14    DO 15 I=3,33                                                      GMR 1750                                                         
CCC   IF(WVLTH.LT.(.5*(WVLTAB(I)+WVLTAB(I+1)))) GO TO 16                GMR 1760                                                         
      IF(FREQ.LT.WVNTBL(I)) GO TO 16                                    GMR 1770                                                         
   15 CONTINUE                                                          GMR 1780                                                         
CCC   SET ILOW EQUAL TO 1 LESS THAN DO MAX                              GMR 1790                                                         
CCC   ILOW=24                                                           GMR 1800                                                         
      I=34                                                              GMR 1810                                                         
CCC   GO TO 18                                                          GMR 1820                                                         
CCC16 ILOW = I-2                                                        GMR 1830                                                         
16    ILOW=I-3                                                          GMR 1840                                                         
   18 CONTINUE                                                          GMR 1850                                                         
CCC   DO 190 I=2,7                                                      GMR 1860                                                         
      DO 190 K=3,7                                                      GMR 1870                                                         
CCC   IF (RATE. LT.(.5*(RATTAB(I)+RATTAB(I+1))))GO TO 195               GMR 1880                                                         
      IF(RATE.LT.RATTAB(K)) GO TO 195                                   GMR 1890                                                         
  190 CONTINUE                                                          GMR 1900                                                         
CCC   KMIN=6                                                            GMR 1910                                                         
      K=8                                                               GMR 1920                                                         
CCC   GO TO 198                                                         GMR 1930                                                         
CC195 KMIN=I-2                                                          GMR 1940                                                         
195   KMIN=K-3                                                          GMR 1950                                                         
  198 CONTINUE                                                          GMR 1960                                                         
      DO 20 J=1,JMAX                                                    GMR 1970                                                         
      IJ = ILOW + J                                                     GMR 1980                                                         
      X(J) =       WVNTBL(IJ)                                           GMR 1990                                                         
   20 CONTINUE                                                          GMR 2000                                                         
C        INTERPOLATE                                                    GMR 2010                                                         
CCC   Z = -ALOG(FREQ)                                                   GMR 2020                                                         
CCC   DO 25 K=1,3                                                       GMR 2030                                                         
      DO 25 K=1,4                                                       GMR 2040                                                         
      KJ=KMIN+K                                                         GMR 2050                                                         
      RATES(K)=RATTAB(KJ)                                               GMR 2060                                                         
      DO 24 J=1,JMAX                                                    GMR 2070                                                         
      IJ = ILOW + J                                                     GMR 2080                                                         
      Y(J)= ALOG(ATTAB(IJ,KJ))                                          GMR 2090                                                         
   24 CONTINUE                                                          GMR 2100                                                         
      ATTN(K)=EXP(AITK(X,Y,FREQ,JMAX) )                                 GMR 2110                                                         
   25 CONTINUE                                                          GMR 2120                                                         
C        APPLY TEMPERATURE CORRECTION                                   GMR 2130                                                         
      DO 31 I=2,5                                                       GMR 2140                                                         
      IF(T.LT.TMPTAB(I)) GO TO 33                                       GMR 2150                                                         
   31 CONTINUE                                                          GMR 2160                                                         
      ILOW = 4                                                          GMR 2170                                                         
      GO TO 35                                                          GMR 2180                                                         
   33 ILOW = I-1                                                        GMR 2190                                                         
   35 CONTINUE                                                          GMR 2200                                                         
      DO 41 J=2,8                                                       GMR 2210                                                         
      IF(FREQ.LT.TFREQ(J)) GO TO 43                                     GMR 2220                                                         
   41 CONTINUE                                                          GMR 2230                                                         
CCC   JLOW IS 2 LESS THAN DO MAX                                        GMR 2240                                                         
      JLOW=6                                                            GMR 2250                                                         
      GO TO 45                                                          GMR 2260                                                         
   43 JLOW = J-2                                                        GMR 2270                                                         
   45 CONTINUE                                                          GMR 2280                                                         
      DO 50 K=1,2                                                       GMR 2290                                                         
      DO 49 J=1,2                                                       GMR 2300                                                         
C        INTERPOLATE IN TEMPERATURE                                     GMR 2310                                                         
CCC   KJ=(KMIN/2)+K                                                     GMR 2320                                                         
      KJ=K+(KMIN+1)/2                                                   GMR 2330                                                         
      JI = JLOW + J                                                     GMR 2340                                                         
      FAC = ((TMPTAB(ILOW)-T)*FACTOR(ILOW+1,JI,KJ)+(T-TMPTAB(ILOW+1))*  GMR 2350                                                         
     1 FACTOR(ILOW,JI,KJ))/(TMPTAB(ILOW)-TMPTAB(ILOW+1))                GMR 2360                                                         
      JI = JLOW +3-J                                                    GMR 2370                                                         
      FACIT(J) = (TFREQ(JI)-FREQ )*FAC                                  GMR 2380                                                         
   49 CONTINUE                                                          GMR 2390                                                         
      TFACT(K) = (FACIT(2)-FACIT(1))/(TFREQ(JLOW+1)-TFREQ(JLOW+2))      GMR 2400                                                         
   50 CONTINUE                                                          GMR 2410                                                         
C        COMPUTE ATTENUATION (DB/KM)                                    GMR 2420                                                         
CCC   KJ=2*KMIN/2+1                                                     GMR 2430                                                         
      KJ=2*((KMIN+1)/2)+1                                               GMR 2440                                                         
CCC   GMRAIN=AITK(RATES,ATTN,RATE,3)*                                   GMR 2450                                                         
      GMRAIN=AITK(RATES,ATTN,RATE,4)*                                   GMR 2460                                                         
     1((RATE-RATTAB(KJ))*TFACT(2)+(RATTAB(KJ+2)-RATE)*                  GMR 2470                                                         
     2TFACT(1))/(RATTAB(KJ+2)-RATTAB(KJ))                               GMR 2480                                                         
CCC                                                                     GMR 2490                                                         
CCC    APPLY CONVERSION TO NEPERS                                       GMR 2500                                                         
CCC                                                                     GMR 2510                                                         
      RETURN                                                            GMR 2520                                                         
      END                                                               GMR 2530                                                         
      SUBROUTINE CIRRUS(CTHIK,CALT,ISEED,CPROB,CEXT)                    CIR  100                                                         
C*********************************************************************  CIR  110                                                         
C*  ROUTINE TO GENERATE ALTITUDE PROFILES OF CIRRUS DENSITY         **  CIR  120                                                         
C*  PROGRAMMED BY   M.J. POST                                       **  CIR  130                                                         
C*                  R.A. RICHTER        NOAA/WPL                    **  CIR  140                                                         
C*                                      BOULDER, COLORADO           **  CIR  150                                                         
C*                                      01/27/1981                  **  CIR  160                                                         
C*                                                                  **  CIR  170                                                         
C*  INPUTS!                                                         **  CIR  180                                                         
C*           CHTIK    -  CIRRUS THICKNESS (KM)                      **  CIR  190                                                         
C*                       0 = USE THICKNESS STATISTICS               **  CIR  200                                                         
C*                       .NE. 0 = USER DEFINES THICKNESS            **  CIR  210                                                         
C*                                                                  **  CIR  220                                                         
C*           CALT     -  CIRRUS BASE ALTITUDE (KM)                  **  CIR  230                                                         
C*                       0 = USE CALCULATED VALUE                   **  CIR  240                                                         
C*                       .NE. 0 = USER DEFINES BASE ALTITUDE        **  CIR  250                                                         
C*                                                                  **  CIR  260                                                         
C*           ICIR     -  CIRRUS PRESENCE FLAG                       **  CIR  270                                                         
C*                       0 = NO CIRRUS                              **  CIR  280                                                         
C*                       .NE. 0 = USE CIRRUS PROFILE                **  CIR  290                                                         
C*                                                                  **  CIR  300                                                         
C*           MODEL    -  ATMOSPHERIC MODEL                          **  CIR  310                                                         
C*                       1-5  AS IN MAIN PROGRAM                    **  CIR  320                                                         
C*                       MODEL = 0,6,7 NOT USED SET TO 2            **  CIR  330                                                         
C*                                                                  **  CIR  340                                                         
C*           ISEED    -  RANDOM NUMBER INITIALIZATION FLAG.         **  CIR  350                                                         
C*                       0 = USE DEFAULT MEAN VALUES FOR CIRRUS     **  CIR  360                                                         
C*                       .NE. 0 = INITIAL VALUE OF SEED FOR RANF    **  CIR  370                                                         
C*                       FUNCTION. CHANGE SEED VALUE EACH RUN FOR   **  CIR  380                                                         
C*                       DIFFERENT RANDOM NUMBER SEQUENCES. THIS    **  CIR  390                                                         
C*                       PROVIDES FOR STATISTICAL DETERMINATION     **  CIR  400                                                         
C*                       OF CIRRUS BASE ALTITUDE AND THICKNESS.     **  CIR  410                                                         
C*                                                                  **  CIR  420                                                         
C*  OUTPUTS!                                                        **  CIR  430                                                         
C*         CTHIK        -  CIRRUS THICKNESS (KM)                    **  CIR  440                                                         
C*         CALT         -  CIRRUS BASE ALTITUDE (KM)                **  CIR  450                                                         
C*         DENSTY(16,I) -  ARRAY, ALTITUDE PROFILE OF CIRRUS DENSITY**  CIR  460                                                         
C*         CPROB        -  CIRRUS PROBABILITY                       **  CIR  470                                                         
C*                                                                  **  CIR  480                                                         
C*********************************************************************  CIR  490                                                         
C                                                                       CIR  500                                                         
      COMMON /CARD1/ MODEL,ITYPE,IEMSCT,M1,M2,M3,IM,NOPRNT,TBOUND,SALB  CIR  510                                                         
      COMMON /CARD2/ IHAZE,ISEASN,IVULCN,ICSTL,ICLD,IVSA,VIS,WSS,WHH,   CIR  520                                                         
     1    RAINRT                                                        CIR  530                                                         
      COMMON RELHUM(34),HSTOR(34),ICH(4),VH(17),TX(63),W(63)            CIR  540                                                         
      COMMON             WPATH(68, 63),TBBY(68)                         CIR  550                                                         
      COMMON ABSC(5,47),EXTC(5,47),ASYC(5,47),VX2(47),AWCCON(5)         CIR  560                                                         
      COMMON /CNTRL/ KMAX,M,IKMAX,NL,ML,IKLO,ISSGEO,IMULT               CIR  570                                                         
      COMMON /CARD4/ V1,V2,DV                                           CIR  580                                                         
      COMMON /MODEL/ Z(34),PM(34),TM(34),RFNDX(34),DENSTY(63,34),       CIR  590                                                         
     1 CLDAMT(34),RRAMT(34),EQLWC(34),HAZEC(34)                         CIR  600                                                         
      DIMENSION CBASE(5,2),TSTAT(11),PTAB(5),CAMEAN(5)                  CIR  610                                                         
      DIMENSION CBASE1(5),CBASE2(5)                                     CIR  620                                                         
      EQUIVALENCE (CBASE1(1),CBASE(1,1)),(CBASE2(1),CBASE(1,2))         CIR  630                                                         
C                                                                       CIR  640                                                         
      DATA  CAMEAN           / 11.0, 10.0, 8.0, 7.0, 5.0 /              CIR  650                                                         
      DATA  PTAB           / 0.8, 0.4, 0.5, 0.45, 0.4/                  CIR  660                                                         
      DATA  CBASE1            / 7.5, 7.3, 4.5, 4.5, 2.5 /               CIR  670                                                         
      DATA  CBASE2            /16.5,13.5,14.0, 9.5,10.0 /               CIR  680                                                         
      DATA  TSTAT             / 0.0,.291,.509,.655,.764,.837,.892,      CIR  690                                                         
     + 0.928, 0.960, 0.982, 1.00 /                                      CIR  700                                                         
C                                                                       CIR  710                                                         
C  SET CIRRUS PROBABILITY AND PROFILE TO ALL ZEROES                     CIR  720                                                         
C                                                                       CIR  730                                                         
      CPROB = 0.0                                                       CIR  740                                                         
      MDL = MODEL                                                       CIR  750                                                         
C                                                                       CIR  760                                                         
      DO 10 I=1,34                                                      CIR  770                                                         
10    DENSTY(16,I)=0.                                                   CIR  780                                                         
C                                                                       CIR  790                                                         
C  CHECK IF USER WANTS TO USE A THICKNESS VALUE HE PROVIDES, CALCULATE  CIR  800                                                         
C  A STATISTICAL THICKNESS, OR USE A MEAN THICKNESS (ISEED = 0).        CIR  810                                                         
C  DEFAULTED MEAN CIRRUS THICKNESS IS 1.0 KM.                           CIR  820                                                         
C                                                                       CIR  830                                                         
      IF ( CTHIK .GT. 0.0 ) GO TO 25                                    CIR  840                                                         
      IF ( ISEED .NE. 0 ) GO TO 15                                      CIR  850                                                         
      CTHIK = 1.0                                                       CIR  860                                                         
      GO TO 25                                                          CIR  870                                                         
C                                                                       CIR  880                                                         
C  CALCULATE CLOUD THICKNESS USING LOWTRAN CIRRUS THICKNESS STATISTICS  CIR  890                                                         
C  NOTE - THIS ROUTINE USES A UNIFORM RANDOM NUMBER GENERATOR           CIR  900                                                         
C  FUNCTION (RANF) WHICH RETURNS A NUMBER BETWEEN 0 AND 1.              CIR  910                                                         
C  THIS FEATURE IS MACHINE DEPENDENT!!                                  CIR  920                                                         
C                                                                       CIR  930                                                         
   15 CALL RANSET(ISEED)                                                CIR  940                                                         
      URN = RANFUN(IDUM)                                                CIR  950                                                         
      DO 20 I = 1, 10                                                   CIR  960                                                         
         IF (URN .GE. TSTAT(I) .AND. URN .LT. TSTAT(I+1)) CTHIK = I-1   CIR  970                                                         
   20 CONTINUE                                                          CIR  980                                                         
      CTHIK = CTHIK / 2.0  +  RANFUN(IDUM) / 2.0                        CIR  990                                                         
C                                                                       CIR 1000                                                         
C  DENCIR IS CIRRUS DENSITY IN KM-1                                     CIR 1010                                                         
C                                                                       CIR 1020                                                         
25    IF(CEXT .GT. 0.) THEN                                             CIR 1030                                                         
           DENCIR = CEXT / 2.                                           CIR 1040                                                         
      ELSE                                                              CIR 1050                                                         
           DENCIR = 0.07 * CTHIK                                        CIR 1060                                                         
      ENDIF                                                             CIR 1070                                                         
C                                                                       CIR 1080                                                         
C  BASE HEIGHT CALCULATIONS                                             CIR 1090                                                         
C                                                                       CIR 1100                                                         
      IF ( MODEL .LT. 1  .OR.  MODEL .GT. 5 ) MDL = 2                   CIR 1110                                                         
      CPROB = 100.0 * PTAB(MDL)                                         CIR 1120                                                         
C                                                                       CIR 1130                                                         
      HMAX = CBASE(MDL,2) - CTHIK                                       CIR 1140                                                         
      BRANGE = HMAX - CBASE(MDL,1)                                      CIR 1150                                                         
      IF ( CALT .GT. 0.0 ) GO TO 27                                     CIR 1160                                                         
      IF ( ISEED .NE. 0 ) GO TO 26                                      CIR 1170                                                         
      CALT = CAMEAN(MDL)                                                CIR 1180                                                         
      GO TO 27                                                          CIR 1190                                                         
   26 CALT = BRANGE * RANFUN(IDUM)+ CBASE(MDL,1)                        CIR 1200                                                         
C                                                                       CIR 1210                                                         
C  PUT CIRRUS DENSITY IN CORRECT ALTITUDE BINS. IF MODEL = 7,           CIR 1220                                                         
C  INTERPOLATE EH(16,I) FOR NON-STANDARD ALTITUDE BOUNDARIES.           CIR 1230                                                         
C                                                                       CIR 1240                                                         
   27 IF(MODEL .EQ. 7) GO TO 60                                         CIR 1250                                                         
      IV1=INT(CALT )                                                    CIR 1260                                                         
      IV2=INT(CALT+CTHIK )                                              CIR 1270                                                         
      DO 30 I = 2, 16                                                   CIR 1280                                                         
         IF(I .GE. IV1 .AND. I .LE. IV2) DENSTY(16,I+1) =  DENCIR       CIR 1290                                                         
   30 CONTINUE                                                          CIR 1300                                                         
C                                                                       CIR 1310                                                         
C  ADJUST FIRST AND LAST CIRRUS LEVEL IF CLOUD DOES NOT ENTIRELY        CIR 1320                                                         
C  FILL EACH LEVEL.                                                     CIR 1330                                                         
C                                                                       CIR 1340                                                         
      IHGT1 = INT( CALT )                                               CIR 1350                                                         
      IHGT2 = INT( CALT + CTHIK)                                        CIR 1360                                                         
      IF( IHGT1 . NE . IHGT2 ) GO TO 35                                 CIR 1370                                                         
      DENSTY(16,IHGT1+1) = DENSTY( 16,IHGT1+1)*CTHIK                    CIR 1380                                                         
      RETURN                                                            CIR 1390                                                         
   35 PCT1  = 1.0 - ( CALT - IHGT1 )                                    CIR 1400                                                         
      DENSTY(16,IHGT1+1) = DENSTY(16,IHGT1+1) * PCT1                    CIR 1410                                                         
      PCT2 =  ( CALT + CTHIK) - IHGT2                                   CIR 1420                                                         
      DENSTY(16,IHGT2+1) = DENSTY(16,IHGT2+1) * PCT2                    CIR 1430                                                         
      RETURN                                                            CIR 1440                                                         
C                                                                       CIR 1450                                                         
C  INTERPOLATE DENSTY(16,I) FOR USER SUPPLIED ALTITUDE BOUNDARIES       CIR 1460                                                         
C                                                                       CIR 1470                                                         
   60 TOP = CALT + CTHIK                                                CIR 1480                                                         
      BOTTOM = CALT                                                     CIR 1490                                                         
      IF (TOP .LT. Z(1)) RETURN                                         CIR 1500                                                         
      IF (BOTTOM .GT. Z(ML)) RETURN                                     CIR 1510                                                         
      IML = ML - 1                                                      CIR 1520                                                         
      DO 70 I=1,IML                                                     CIR 1530                                                         
         ZMIN = Z(I)                                                    CIR 1540                                                         
         ZMAX = Z(I+1)                                                  CIR 1550                                                         
         DENOM = ZMAX - ZMIN                                            CIR 1560                                                         
         IF(BOTTOM.LE.ZMIN .AND. TOP.GE.ZMAX) DENSTY(16,I) = DENCIR     CIR 1570                                                         
         IF(BOTTOM.GE.ZMIN .AND. TOP.LT.ZMAX)                           CIR 1580                                                         
     +        DENSTY(16,I) = DENCIR * CTHIK/DENOM                       CIR 1590                                                         
         IF(BOTTOM.GE.ZMIN .AND. TOP.GE.ZMAX .AND. BOTTOM.LT.ZMAX)      CIR 1600                                                         
     +        DENSTY(16,I) = DENCIR * (ZMAX - BOTTOM)/ DENOM            CIR 1610                                                         
         IF(BOTTOM.LT.ZMIN .AND. TOP.LE.ZMAX .AND.TOP.GT.ZMIN)          CIR 1620                                                         
     +        DENSTY(16,I) = DENCIR * (TOP - ZMIN) / DENOM              CIR 1630                                                         
   70 CONTINUE                                                          CIR 1640                                                         
      RETURN                                                            CIR 1650                                                         
      END                                                               CIR 1660                                                         
      FUNCTION   RANFUN(IDUM)                                           RAN  100                                                         
      RANFUN=RANF()                                                     RAN  110                                                         
      RETURN                                                            RAN  120                                                         
      END                                                               RAN  130                                                         
      FUNCTION   ABSLIM(TK,AWLWC)                                       ALM  100                                                         
CCC                                                                     ALM  110                                                         
CCC    FOR CLOUD OR AEROSOL ATTENUATION AS FREQ APPROACHES ZERO         ALM  120                                                         
CCC    MODIFIED DEBYE EQUATIONS FROM RAY (1972) APPL. OPTICS VOL 11     ALM  130                                                         
CCC                                                                     ALM  140                                                         
CCC    ANO= 8.0*10**(-2)  (CM-4)                                        ALM  150                                                         
CCC    ALM= 41.*RR**(-0.21)  (CM-1)  RR IN (MM/HR)                      ALM  160                                                         
CCC                                                                     ALM  170                                                         
      DATA PI/3.14159265/                                               ALM  180                                                         
CCC   ANO=0.08                                                          ALM  190                                                         
CCC   ALM=41./RR**0.21                                                  ALM  200                                                         
